import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import {} from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
import DataTable from "react-data-table-component";

const ManageProducts = ({ auth: { user } }) => {
  const [productsData, setProductsData] = useState([]);

  const getProductsData = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/usersFetching");
      setProductsData(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  const Collumns = [
    {
      name: "First Name",
      selector: (row) => row.fname,
    },
    {
      name: "Last Name",
      selector: (row) => row.lname,
    },
    {
      name: "Email Address",
      selector: (row) => row.email,
    },
    {
      name: "Phone",
      selector: (row) => row.phn,
    },
    
    {
      name: "Password",
      selector: (row) => row.pass,
    },

    {
      name: "Role",
      selector: (row) => row.role,
    },
    {
      name: "Status",
      selector: (row) => row.status,
    },
    {
      name: "Email Status",
      selector: (row) => row.EmailPending,
    },
    {
      name: "View",
        cell: (row) => (
          <a href={`/admin/users-details/${row._id}`} className="btn btn-success">
            <i class="fa fa-eye"></i>
          </a>
        ),
      },
      {
        name: "Edit",
          cell: (row) => (
            <a href={`/admin/users-edit/${row._id}`} className="btn btn-primary">
              <i class="fa fa-pen"></i>
            </a>
          ),
        },
    
    
        {
          name: "Delete",
          cell: (row) => <button className="btn btn-danger"  onClick={() => deleteUsers(row._id)}><i className="fas fa-user-times"/></button>,
        },
    
    
        {
          name: "Suspend",
          cell: (row) => <button className="btn btn-warning"  onClick={() => suspenduser(row._id)}> <i className="fa fa-ban"/></button>,
        },
  ];

  useEffect(() => {
    getProductsData();
  }, []);




  const deleteUsers=async(id)=>{
    const res2=await axios.delete(`http://localhost:5000/deleteusers/${id}`,{
    headers:{
  
       "Content-Type":"application/json",
    }
    });
    const deleteData=await res2.json();
    console.log(deleteData);
    if(res2.status===422 || !deleteData){
      console.log("Error");
    }
    else{
      alert("ok")
    }
  }


 
  const suspenduser=async(id)=>{
    const res2=await axios.patch(`http://localhost:5000/suspendusers/${id}`,{
    headers:{
  
       "Content-Type":"application/json",
    }
    });
    const suspendeduser=await res2.json();
    console.log(suspendeduser);
    if(res2.status===422 || !suspendeduser){
      console.log("error");
    }
    else{
      console.log("User Suspended");
    }
  }




  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
