import React, { useState, useEffect } from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import "../../css/landing.css";
import Footer from "../common/Footer";
import axios from "axios";
// import Item from "./item";
// import Carousel2 from "react-elastic-carousel";
// import Col from "react-bootstrap/Col";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // Requires a Loader
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Logo from "../../images/virtual-art-gif2.gif";
import Form from "react-bootstrap/Form";

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 2 },
  { width: 768, itemsToShow: 4 },
  { width: 1200, itemsToShow: 4 },
];

const Landing = ({ isAuthenticated }) => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [fname, setfname] = useState("");
  const [lname, setlname] = useState("");
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [country_name, setcountry_name] = useState("");

  // const history = useHistory();

  const setFname = (e) => {
    const { value } = e.target;
    setfname(value);
  };

  const setLname = (e) => {
    const { value } = e.target;
    setlname(value);
  };

  const setEmail = (e) => {
    const { value } = e.target;
    setemail(value);
  };

  const setPassword = (e) => {
    const { value } = e.target;
    setpassword(value);
  };

  const setCountryName = (e) => {
    const { value } = e.target;
    setcountry_name(value);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("fname", fname);
    formData.append("lname", lname);
    formData.append("email", email);
    formData.append("password", password);
    formData.append("country_name", country_name);

    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    await axios
      .post("http://localhost:5000/register", formData, config)
      .then((data) => {
        console.log(data, "Category Added");
        if (data.status === 200) {
          alert(`Signed Up Successfully`);
          window.location.href = "/auctions";
        }
      });
  };

  const IsLoggedIn = window.localStorage.getItem("userLoggedIn");
   
     if(IsLoggedIn!=="true"){
    return <Redirect to="/user/login" />;
     }
     

  if (isAuthenticated) {
    return <Redirect to="/admin/dashboard" />;
  }

  return (
    <>
      <div className="navNsec m-0">
        <div className="auctions">
         <div className="create-auctions">
               <form action="">
                <div className="form-group">
                <Form className="col-md-12 form-add p-5 shadow-lg rounded my-5">
                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Title</Form.Label>
                  <Form.Control
                    type="text"
                    name="collection_title"
                    onChange={setCollectionsTitle}
                    placeholder="Art Collections Title"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="artCollectionss_name"
                    onChange={setCollectionsName}
                    placeholder="Art Collections Name"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Short Desc</Form.Label>
                  <Form.Control
                    type="text"
                    name="artCollectionss_short_desc"
                    onChange={setShortDesc}
                    placeholder="Art Collections Short Desc"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections's Category</Form.Label>
                  <Form.Select
                    name="artCollectionss_category"
                    className="form-control"
                    onChange={setCollectionCategory}
                  >
                    <option>---------</option>
                    {categoryData.map((e) => {
                      return (
                        <option key={e.category_name}>{e.category_name}</option>
                      );
                    })}
                  </Form.Select>
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Price</Form.Label>
                  <Form.Control
                    type="number"
                    name="artCollectionss_price"
                    onChange={stCollectionPrice}
                    placeholder="Art Collections Price"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Thumbnail</Form.Label>
                  <Form.Control
                    type="file"
                    name="collectionsImg"
                    onChange={setimgfile}
                    placeholder="Art Collections Thumbnail"
                  />
                </Form.Group>

                <Form.Group
                  className=""
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button variant="primary" type="submit" onClick={addUserData}>
                    Submit
                  </Button>
                </Form.Group>
              </Form>
                </div>
               </form>
          </div>                      
        </div>
      </div>

      <br />
      <br />

      <Footer />
    </>
  );
};

Landing.propTypes = {
  isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(Landing);
