import React, { useState,useEffect} from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import AsideBar from "./common/AsideBar";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useParams } from "react-router-dom";
import Footer from "./common/Footer";
const UserManagement = ({ auth: { user } }) => {
  //Inserting Form Data
  

  const [newUser, setNewUser] = useState({
    language: "",
    company_name: "",
    cpoyright_text: "",
    logo: "",
  });

  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };


  const { id } = useParams();



  const getUserData = async () => {
    try {
      const resp = await axios.get(`http://localhost:5000/FetchGeneralsettings`);
      setNewUser(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUserData();
  },[]);


  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("language", newUser.language);
    formData.append("company_name", newUser.company_name);
    formData.append("cpoyright_text", newUser.cpoyright_text);
    formData.append("logo",  newUser.logo);

 const updateUsers=axios({
      method: "POST",
      url: `http://localhost:5000/Add-generalsetings`,
      data: JSON.stringify(newUser),
      headers: { "Content-Type": "application/json" },
    });
      if(updateUsers){
        console.log(updateUsers);
      }
      else{
        console.log("No update users");
      }
  };



  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="/" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5 className="d-flex justify-content-center">
                    Welcome, {user && user.name}
                  </h5>
                </div>
              
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">Add Users</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#/">User Management</a>
                    </li>
                    <li className="breadcrumb-item active">Add Users</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <div></div>
        
          <div className="container">
            <div className="row">
              <div className="col-md-12 p-5">
                  <h4>General Settings</h4>
                  <Form className="mt-3 shadow-lg bg-light p-5 rounded"
                  onSubmit={handleSubmit}>
                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Language</Form.Label>
                  <Form.Control
                    type="text"
                    name="language"
                    // value={newUser.language}
                    onChange={handleChange}
                    placeholder="Language"
                    required
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Company's Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="company_name"
                    // value={newUser.company_name}
                    onChange={handleChange}
                    placeholder="Company's Name"
                    required
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Copyright Text</Form.Label>
                  <Form.Control
                    type="text"
                    name="cpoyright_text"
                    value={newUser.cpoyright_text}
                    onChange={handleChange}
                    placeholder="Copyright Text"
                    required
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Site Logo</Form.Label>
                  <Form.Control
                    type="file"
                    name="logo"
                    onChange={(e)=>e.target.files[0]}
                    placeholder="Site Logo"
                    required
                  />
                </Form.Group>

                <Form.Group
                  className=""
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button className="btn btn-outline-info text-light" variant="primary" type="submit">
                    Update
                  </Button>
                </Form.Group>
              </Form>

                 
              </div>
            </div>
          </div>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
UserManagement.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(UserManagement);
