import React, { useState, useEffect } from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import "../../css/landing.css";
import Footer from "../common/Footer";
import axios from "axios";
import sliderVr from "../../images/slider_vr.webp"
// import Item from "./item";
// import Carousel2 from "react-elastic-carousel";
// import Col from "react-bootstrap/Col";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import testimonial from '../../images/th.jfif'

const breakPoints = [
    { width: 1, itemsToShow: 1 },
    { width: 550, itemsToShow: 2 },
    { width: 768, itemsToShow: 4 },
    { width: 1200, itemsToShow: 4 },
];


const Landing = ({ isAuthenticated }) => {
    // Categories Fetching


    // Fetching The First Wall Collection   
    const [CollectionData1, setCollectionData1] = useState([]);

    const getCollectionData1 = async () => {
        try {
            const resp = await axios.get("http://localhost:5000/Fetch-GeneralSettings");
            setCollectionData1(resp.data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getCollectionData1();
    }, []);


    let img = undefined;
    let cmp_name = undefined

    {
        CollectionData1 &&
            CollectionData1.map((cd) => (

                img = require(`../../uploads/${cd.logo}`).default,
                cmp_name = cd.company_name

            ));
    }




    // Fetching The First Wall Collection   
    const [CollectionDataIndividually1, setCollectionDataIndividually1] = useState([]);

    const getCollectionDataIndividually1 = async () => {
        try {
            const resp = await axios.get("http://localhost:5000/Fetch-Products-Individually1");
            setCollectionDataIndividually1(resp.data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getCollectionDataIndividually1();
    }, []);


    let collectionImg1 = undefined;
    let price1 = undefined;
    let collection_name1 = undefined;


    {
        CollectionDataIndividually1 &&
            CollectionDataIndividually1.map((cd) => (

                collectionImg1 = require(`../../uploads/${cd.collectionsImg}`).default,
                price1 = cd.artCollectionss_price,
                collection_name1 = cd.artCollectionss_name


            ))
    }


    //  // Fetching The Second Wall Collection   
    const [CollectionData2, setCollectionData2] = useState([]);

    const getCollectionData2 = async () => {
        try {
            const resp = await axios.get("http://localhost:5000/Fetch-Products-Individually2");
            setCollectionData2(resp.data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getCollectionData2();
    }, []);


    let img2 = undefined;
    let price2 = undefined;
    let coll_name2 = undefined;

    {
        CollectionData2 &&
            CollectionData2.map((cd) => (

                img2 = require(`../../uploads/${cd.collectionsImg}`).default,
                price2 = cd.artCollectionss_price,
                coll_name2 = cd.artCollectionss_name

            ))
    }




    //  // Fetching The Third Wall Collection   
    const [CollectionData3, setCollectionData3] = useState([]);

    const getCollectionData3 = async () => {
        try {
            const resp = await axios.get("http://localhost:5000/Fetch-Products-Individually3");
            setCollectionData3(resp.data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getCollectionData3();
    }, []);


    let img3 = undefined;
    let price3 = undefined;
    let coll_name3 = undefined;

    {
        CollectionData3 &&
            CollectionData3.map((cd) => (

                img3 = require(`../../uploads/${cd.collectionsImg}`).default,
                price3 = cd.artCollectionss_price,
                coll_name3 = cd.artCollectionss_name
            ))
    }





    //   // Fetching The Fourth Wall Collection   
    const [CollectionData4, setCollectionData4] = useState([]);

    const getCollectionData4 = async () => {
        try {
            const resp = await axios.get("http://localhost:5000/Fetch-Products-Individually4");
            setCollectionData4(resp.data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getCollectionData4();
    }, []);


    let img4 = undefined;
    let price4 = undefined;
    let coll_name4 = undefined;

    {
        CollectionData4 &&
            CollectionData4.map((cd) => (

                img4 = require(`../../uploads/${cd.collectionsImg}`).default,
                price4 = cd.artCollectionss_price,
                coll_name4 = cd.artCollectionss_name

            ))
    }





    if (isAuthenticated) {
        return <Redirect to="/admin/dashboard" />;
    }


    return (
        <>
            <div className="navNsec m-0">



                <div className="container-fluid">
                    <div className="row">
                        <hr className="text-light" />
                        <div className="col-md-12 bg-dark p-2">
                            <ul className="list-unstyled d-flex p-3">
                                <li className="list-inline-item"><a className="text-light" href="/">Calenders</a></li>
                                <li className="list-inline-item"><a className="text-light" href="/">Auction Results</a></li>
                            </ul>
                        </div>
                        <hr className="text-light" />

                    </div>
                </div>
                <h2 className="d-flex">COMING SOON</h2>
                <div className="container">
                    <div className="row">

                        <div className="col-md-4 d-flex Vr">
                            <p>Virtual Art Gallery</p>
                        </div>
                        <div className="col-md-6">
                            <h4 className="d-flex">Auctions are Loading __ </h4>
                            <p className="d-flex">The next Virtual Art Gallerie's auction will be announced shortly.</p>
                        </div>
                    </div>
                </div>



            </div>


            <Footer />

        </>
    );
};

Landing.propTypes = {
    isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(Landing);
