import React, { useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
// import icon from "../img/user.png";
import {} from "../actions/auth";
import axios  from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";

const AddSubCategory = ({ auth: { user } }) => {
  const [category_name, setcategory_name] = useState("");
  const [category_title, setcategory_title] = useState("");
  const [category_desc, setcategory_desc] = useState("");
  

  const [file, setFile] = useState("");

  // const history = useHistory();

  const setdata = (e) => {
    const { value } = e.target;
    setcategory_name(value);
    setcategory_title(value);
    setcategory_desc(value);
  };

  const setimgfile = (e) => {
    setFile(e.target.files[0]);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("category_name", category_name);
    formData.append("category_image", file);
    formData.append("category_title", category_title);
    formData.append("category_desc", category_desc);

    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    const res = await axios.post(
      "http://localhost:5000/Add-Categories",
      formData,
      config
    ).then(() => {
      // Once posted, the user will be notified
      alert("Successfully Added The Category");
    });
  };

  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
       <AsideBar/>
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Add Sub Category</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <form
                className="form"               
                encType={"multipart/form-data"}
                
              >
                <div className="form-group">
                  <label htmlFor="sub_category_name">Sub Category Name: </label>
                  <input
                    type="text"
                    className="form-control"
                    name="sub_category_name"
                    value={subCategoryData.sub_category_name}
                    onChange={handleInputs}
                    placeholder="Category Name"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="sub_category_image">
                    {" "}
                   Sub Category Image/Logo:{" "}
                  </label>
                  <input
                    type="file"
                    className="form-control"
                    name="sub_category_image" 
                    value={subCategoryData.sub_category_image}
                    onChange={handleInputs}
                    placeholder="Category Image/Logo"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="sub_category_title"> Sub Category Title: </label>
                  <input
                    type="text"
                    className="form-control"
                    name="sub_category_title"
                    value={subCategoryData.sub_category_title} 
                    onChange={handleInputs}
                    placeholder="Category Title"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="sub_category_desc"> Sub Category Descriptions: </label>
                  <input
                    type="text"
                    className="form-control"
                    name="sub_category_desc"
                    value={subCategoryData.sub_category_desc} 
                    onChange={handleInputs}
                    placeholder="Category Descriptions"
                  />
                </div>

                

                <div className="form-group" align="center">
                  <input
                   onClick={AddSubsubCategoryData}
                    type="submit"
                    className="btn btn-outline-primary"
                    value={"Add Subcategory"}
                  />
                </div>
              </form>
            </div>
          </div>
        </section>
      </div>
      <div>
       <Footer/>
      </div>
    </div>
  );
};
AddSubCategory.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(AddSubCategory);
