import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Footer from "../common/Footer";

// import aboutusImg from "../../images_files/about-us.jpg"

const TnCComp = ({ isAuthenticated }) => {

    const [TnC, setTnC] = useState("");

    useEffect(() => {
      const fetchTnC = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-TnC");
        const json = await resp.json();
        if (resp.ok) {
          setTnC(json);
        }
      };
  
      fetchTnC();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
          <div className="col-md-12 d-flex justify-content-end">
            <Breadcrumb>
              <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
              <Breadcrumb.Item active>Terms & Conditions</Breadcrumb.Item>
            </Breadcrumb>
            </div>
            <div className="col-md-12 bg-primary my-3 p-5" align="center" >
            
             {/* <img src={aboutusImg} alt={"About Us"} height={"300px"} width={"100%"}/> */}
             <h2 className="text-dark font-weight-bold">Terms & Conditions</h2>

            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {TnC &&
                      TnC.map((TnC) => (
                        <p
                         href=""
                          key={TnC._id}
                        >
                          {TnC.tnc}
                        </p>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
          <Footer/>
		</section>
	);
};

TnCComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(TnCComp);
