import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import AsideBar from "./common/AsideBar";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useParams } from "react-router-dom";

const CategoryManagement = ({ auth: { user } }) => {
  const [newCategory, setnewCategory] = useState({
    category_name: "",
    category_title: "",
    category_desc: "",
    category_image: "",
  });

  const handleChange = (e) => {
    setnewCategory({ ...newCategory, [e.target.name]: e.target.value });
  };


  const handleFiles = (e) => {
    setnewCategory({ ...newCategory, [e.target.name]: e.target.files[0] });
  };
  const { id } = useParams();
  //    console.log(id);

  const getUserData = async () => {
    try {
      const resp = await axios.get(`http://localhost:5000/getcategory/${id}`);
      setnewCategory(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUserData();
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("category_name", newCategory.category_name);
    formData.append("category_title", newCategory.category_title);
    formData.append("category_desc", newCategory.category_desc);
    formData.append("category_image", newCategory.category_image);


  const updateCategories=  axios({
      method: "post",
      url: `http://localhost:5000/updateCategories/${id}`,
      data: JSON.stringify(newCategory),
      headers: { "Content-Type": "application/json" },
    })
      .then((response) => {
        console.log(response);
      })
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        if (error.response) {
          console.log(error.response.data);
        }
      });
  };

  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="/" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5 className="d-flex justify-content-center">
                    Welcome, {user && user.name}
                  </h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">Category Management</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#/">Category Management</a>
                    </li>
                    <li className="breadcrumb-item active">Add Category</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>

          <section className="content">
            <div className="container-fluid p-5">
              <div className="row">
                <div className="col-md-12">
                  <Form
                    className="mt-3 shadow-lg bg-light p-5 rounded"
                    onSubmit={handleSubmit}
                  >
                    <h4>Add Categories</h4>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                      <Form.Label>Category Name</Form.Label>
                      <Form.Control
                        style={{
                          background: "aliceblue",
                          color: "black",
                          borderRadius: "22px",
                        }}
                        type="text"
                        name="category_name"
                        value={newCategory.category_name}
                        onChange={handleChange}
                        placeholder="Title"
                        className="add-users-input"
                      />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicEmail">
                      <Form.Label>Category Title</Form.Label>
                      <Form.Control
                        style={{
                          background: "aliceblue",
                          color: "black",
                          borderRadius: "22px",
                        }}
                        type="text"
                        name="category_title"
                        value={newCategory.category_title}
                        onChange={handleChange}
                        placeholder="Category Title"
                        className="add-users-input"
                      />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicEmail">
                      <Form.Label>Category Description</Form.Label>
                      <Form.Control
                        style={{
                          background: "aliceblue",
                          color: "black",
                          borderRadius: "22px",
                        }}
                        type="text"
                        name="category_desc"
                        value={newCategory.category_desc}
                        onChange={handleChange}
                        placeholder="Descriptions"
                        className="add-users-input"
                      />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicEmail">
                      <Form.Label>Category Image</Form.Label>
                      <Form.Control
                        style={{
                          background: "aliceblue",
                          color: "black",
                          borderRadius: "22px",
                        }}
                        type="file"
                        name="category_image"
                        onChange={handleFiles}
                        placeholder="Descriptions"
                        className="add-users-input"
                        required
                      />
                    </Form.Group>

                    <Form.Group
                      className="mb-3"
                      controlId="formBasicEmail"
                      align="center"
                    >
                      <Button
                        type="submit"
                        className="btn btn-outline-info text-light w-25 form-control shadow-lg "
                      >
                        Update
                      </Button>
                    </Form.Group>
                  </Form>
                </div>
              </div>
            </div>
          </section>
        </div>
      </div>
      <div>
        <footer className="main-footer">
          <strong>
            Copyright © 2014-2021
            <a href="https://adminlte.io">AdminLTE.io</a>.
          </strong>
          All rights reserved.
          <div className="float-right d-none d-sm-inline-block">
            <b>Version</b> 3.2.0
          </div>
        </footer>
      </div>
    </div>
  );
};
CategoryManagement.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(CategoryManagement);
