    {
      name: "View",
      cell: (row) => (
        <a
          href={`/admin/product-details/${row._id}`}
          className="btn btn-success"
        >
          <i class="fa fa-eye"></i>
        </a>
      ),
    },
    {
      name: "Edit",
      cell: (row) => (
        <a href={`/admin/product-edit/${row._id}`} className="btn btn-primary">
          <i class="fa fa-pen"></i>
        </a>
      ),
    },

    {
      name: "Delete",
      cell: (row) => (
        <button className="btn btn-danger" onClick={() => deleteUsers(row._id)}>
          <i className="fas fa-times" />
        </button>
      ),
    },
  ];

  useEffect(() => {
    getProductsData1();
  }, []);

  // 2nd Wall Collections
  const [productsData2, setProductsData2] = useState([]);

  const getProductsData2 = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/Fetch-Products2");
      setProductsData2(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  const Collumns2 = [
    {
      name: "Title",
      selector: (row) => row.collection_title,
    },
    {
      name: "Name",
      selector: (row) => row.artCollectionss_name,
