    {
      name: "Description",
      selector: (row) => row.artCollectionss_short_desc,
    },
    {
      name: "Category",
      selector: (row) => row.artCollectionss_category,
    },
    {
      name: "Thumbnail",
      selector: (row) => (
        <img
          width={70}
          height={70}
          src={require(`../uploads/${row.collectionsImg}`).default}
          alt={"Florida Fashion"}
        />
      ),
    },
    {
      name: "Price",
      selector: (row) => row.artCollectionss_price,
    },

    {
      name: "Type",
      selector: (row) => row.type,
    },
    {
      name: "Selling Type",
      selector: (row) => row.selling_type,
    },
    {
      name: "Bid Amount(In USD)",
      selector: (row) => row.bid_amount_in_usd,
    },
    {
      name: "Status",
      selector: (row) => row.status,
    },
    {
      name: "View",
      cell: (row) => (
        <a
          href={`/admin/product-details2/${row._id}`}
          className="btn btn-success"
        >
          <i class="fa fa-eye"></i>
        </a>
      ),
    },
    {
      name: "Edit",
      cell: (row) => (
        <a href={`/admin/product-edit2/${row._id}`} className="btn btn-primary">
          <i class="fa fa-pen"></i>
        </a>
      ),
    },

    {
      name: "Delete",
      cell: (row) => (
        <button
          className="btn btn-danger"
          onClick={() => deleteUsers2(row._id)}
        >
          <i className="fas fa-times" />
        </button>
      ),
    },
  ];

  useEffect(() => {
    getProductsData2();
  }, []);

  // 3rd Wall Collections

  const [productsData3, setProductsData3] = useState([]);

  const getProductsData3 = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/Fetch-Products3");
      setProductsData3(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  const Collumns3 = [
    {
      name: "Title",
      selector: (row) => row.collection_title,
    },
    {
      name: "Name",
      selector: (row) => row.artCollectionss_name,
    },
    {
      name: "Description",
      selector: (row) => row.artCollectionss_short_desc,
    },
    {
      name: "Category",
      selector: (row) => row.artCollectionss_category,
    },
    {
      name: "Thumbnail",
      selector: (row) => (
        <img
          width={70}
          height={70}
          src={require(`../uploads/${row.collectionsImg}`).default}
          alt={"Florida Fashion"}
        />
      ),
    },
    {
      name: "Price",
      selector: (row) => row.artCollectionss_price,
    },

    {
      name: "Type",
      selector: (row) => row.type,
    },
    {
      name: "Selling Type",
      selector: (row) => row.selling_type,
    },
    {
      name: "Bid Amount(In USD)",
      selector: (row) => row.bid_amount_in_usd,
    },
    {
      name: "Status",
      selector: (row) => row.status,
    },
    {
      name: "View",
      cell: (row) => (
        <a
          href={`/admin/product-details3/${row._id}`}
          className="btn btn-success"
        >
          <i class="fa fa-eye"></i>
        </a>
      ),
    },
    {
      name: "Edit",
      cell: (row) => (
        <a href={`/admin/product-edit3/${row._id}`} className="btn btn-primary">
          <i class="fa fa-pen"></i>
        </a>
      ),
    },

    {
      name: "Delete",
      cell: (row) => (
        <button
          className="btn btn-danger"
          onClick={() => deleteUsers3(row._id)}
        >
          <i className="fas fa-times" />
        </button>
      ),
    },
  ];

  useEffect(() => {
    getProductsData3();
  }, []);

  const [productsData4, setProductsData4] = useState([]);

  const getProductsData4 = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/Fetch-Products4");
      setProductsData4(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  const Collumns4 = [
    {
      name: "Title",
      selector: (row) => row.collection_title,
    },
    {
      name: "Name",
      selector: (row) => row.artCollectionss_name,
    },
    {
      name: "Description",
      selector: (row) => row.artCollectionss_short_desc,
    },
    {
      name: "Category",
      selector: (row) => row.artCollectionss_category,
    },
    {
      name: "Thumbnail",
      selector: (row) => (
        <img
          width={70}
          height={70}
          src={require(`../uploads/${row.collectionsImg}`).default}
          alt={"Florida Fashion"}
        />
      ),
    },
    {
      name: "Price",
      selector: (row) => row.artCollectionss_price,
    },

    {
      name: "Type",
      selector: (row) => row.type,
    },
    {
      name: "Selling Type",
      selector: (row) => row.selling_type,
    },
    {
      name: "Bid Amount(In USD)",
      selector: (row) => row.bid_amount_in_usd,
    },
    {
      name: "Status",
      selector: (row) => row.status,
    },
    {
      name: "View",
      cell: (row) => (
        <a
          href={`/admin/product-details4/${row._id}`}
          className="btn btn-success"
        >
          <i class="fa fa-eye"></i>
        </a>
      ),
    },
    {
      name: "Edit",
      cell: (row) => (
        <a href={`/admin/product-edit4/${row._id}`} className="btn btn-primary">
          <i class="fa fa-pen"></i>
        </a>
      ),
    },

    {
      name: "Delete",
      cell: (row) => (
        <button
          className="btn btn-danger"
          onClick={() => deleteUsers4(row._id)}
        >
          <i className="fas fa-times" />
        </button>
      ),
    },
  ];

  useEffect(() => {
    getProductsData4();
  }, []);

  // 1st Wall Collections Delete
  const deleteUsers = async (id) => {
    await axios
      .delete(`http://localhost:5000/deletearts/${id}`, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((data) => {
        console.log(data, "Arts Collection Has Been Deleted Successfully");

        if (data.status === 200) {
          alert("Arts Collection Has Been Deleted Successfully");
          window.location.href = "/admin/atrsCollections";
        }
      });
  };

  // 2nd Wall Collections Delete
  const deleteUsers2 = async (id) => {
    await axios
      .delete(`http://localhost:5000/deletearts2/${id}`, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((data) => {
        console.log(data, "Arts Collection Has Been Deleted Successfully");

        if (data.status === 200) {
          alert("Arts Collection Has Been Deleted Successfully");
          window.location.href = "/admin/atrsCollections";
        }
      });
  };

  // 3rd Wall Collections Delete
  const deleteUsers3 = async (id) => {
    await axios
      .delete(`http://localhost:5000/deletearts3/${id}`, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((data) => {
        console.log(data, "Arts Collection Has Been Deleted Successfully");

        if (data.status === 200) {
          alert("Arts Collection Has Been Deleted Successfully");
          window.location.href = "/admin/atrsCollections";
        }
      });
  };

  // 3rd Wall Collections Delete
  const deleteUsers4 = async (id) => {
    await axios
      .delete(`http://localhost:5000/deletearts4/${id}`, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((data) => {
        console.log(data, "Arts Collection Has Been Deleted Successfully");

        if (data.status === 200) {
          alert("Arts Collection Has Been Deleted Successfully");
          window.location.href = "/admin/atrsCollections";
        }
      });
  };

  function calcTime(city, offset) {
    var b = new Date();
    var utc = b.getTime() + b.getTimezoneOffset() * 60000;
    var nd = new Date(utc + 3600000 * offset);
    return nd.getHours();
  }

  var showime = calcTime("Los Angeles", "-8");

  return (
    <div className="wrapper">
      <div></div>
      <div>
        <AsideBar />
      </div>
