                <div className="container-fluid">
                  <div className="row">
                    <div className="col-md-12">
                      <DataTable
                        title="Products' Data"
                        columns={Collumns}
                        data={productsData}
                        pagination
                        fixedHeader
                        selectableRows
                      />
                    </div>
                  </div>
                </div>
              </section>

              <div className="row">
                <div className="col-md-11 m-3" align="right">
                  <a
                    className="btn btn-outline-info"
                    href="/admin/atrsCollections2"
                  >
                    <i className="fa fa-plus"></i> Add Collections
                  </a>
                </div>
              </div>

              <h2 className="products-head p-3">2nd Wall Collections</h2>
              <section className="content">
                <div className="container-fluid">
                  <div className="row">
                    <div className="col-md-12">
                      <DataTable
                        title="Products' Data"
                        columns={Collumns2}
                        data={productsData2}
                        pagination
                        fixedHeader
                        selectableRows
                      />
                    </div>
                  </div>
                </div>
              </section>

              <div className="row">
                <div className="col-md-11 m-3" align="right">
                  <a
                    className="btn btn-outline-info"
                    href="/admin/atrsCollections3"
                  >
                    <i className="fa fa-plus"></i> Add Collections
                  </a>
                </div>
              </div>

              <h2 className="products-head p-3">3rd Wall Collections</h2>
              <section className="content">
