import React, { useState,useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import AsideBar from "./common/AsideBar";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Footer from './common/Footer';


const GeneralSettings = ({ auth: { user } }) => {
  //Inserting Form Data
  const [EmailSettings, setEmailSettings] = useState({
    language:"",
    company_name:"",
    cpoyright_text:"",
    logo:"",
  });

  const handleChange = (e) => {
    setEmailSettings({ ...EmailSettings, [e.target.name]: e.target.value });
  };

  const getUserData = async () => {
    try {
      const resp = await axios.get(`http://localhost:5000/FetchGeneralsettings`);
      setEmailSettings(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUserData();
  }, []);


  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("language", EmailSettings.language);
    formData.append("company_name", EmailSettings.company_name);
    formData.append("cpoyright_text",   EmailSettings.cpoyright_text);
    formData.append("logo",   EmailSettings.logo);

   const addUserRes= axios({
      method: "PATCH",
      url: "http://localhost:5000/Update-generalsetings",
      data: JSON.stringify(EmailSettings),
      headers: { "Content-Type": "application/json" },
    });

    if(addUserRes===true){
      window.location.href="/admin/dashboard"
    }
  };


  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="/" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5 className="d-flex justify-content-center">
                    Welcome, {user && user.name}
                  </h5>
                </div>
                {/* <Link className="d-flex justify-content-center" onClick={logout} to="/" replace>
									<i className="fas fa-sign-out-alt"></i>{" "}
									<span className="hide-sm"> &nbsp;Logout</span>
								</Link>
								<a href="/" >Logout</a> */}
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">General Settings</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#/">General Settings Management</a>
                    </li>
                    <li className="breadcrumb-item active">General Settings</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <div></div>
          <section className="content"></section>
          <div className="container">
            <div className="row">
                <Form className="col-md-12 form-add p-5 shadow-lg rounded my-5">
                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Language</Form.Label>
                    <Form.Control
                      type="text"
                      name="language"
                      value={EmailSettings.language}
                      onChange={handleChange}
                      placeholder="Language"
                    />
                  </Form.Group>

                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Copmpany Name</Form.Label>
                    <Form.Control
                      type="text"
                      name="company_name"
                      value={EmailSettings.company_name}
                      onChange={handleChange}
                      placeholder="Copmpany Name"
                    />
                  </Form.Group>

                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Copyright Text</Form.Label>
                    <Form.Control
                      type="text"
                      name="cpoyright_text"
                      value={EmailSettings.cpoyright_text}
                      onChange={handleChange}
                      placeholder="Copyright Text"
                    />
                  </Form.Group>

                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Site Logo</Form.Label>
                    <Form.Control
                      type="file"
                      name="logo"
                      onChange={(e)=>e.target.files[0]}
                      placeholder="Logo"
                    />
                  </Form.Group>

    

                  <Form.Group
                    className=""
                    controlId="formBasicEmail"
                    align="center"
                  >
                    <Button
                      variant="outline-primary"
                      type="submit"
                      onClick={handleSubmit}
                    >
                      Update
                    </Button>
                  </Form.Group>
                </Form>
            </div>
          </div>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
GeneralSettings.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(GeneralSettings);
