import React, { useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import AsideBar from "./common/AsideBar";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Footer from './common/Footer';


const GeneralSettings = ({ auth: { user } }) => {
  //Inserting Form Data
  const [language, setlanguage] = useState("");
  const [company_name, setcompany_name] = useState("");
  const [cpoyright_text, setcpoyright_text] = useState("");
  const [file, setFile] = useState("");

  // const history = useHistory();

  const setLanguage = (e) => {
    const { value } = e.target;
    setlanguage(value);
  };

  const setCompanyName = (e) => {
    const { value } = e.target;
    setcompany_name(value);
  };

  const setCopyrightText = (e) => {
    const { value } = e.target;
    setcpoyright_text(value);
  };

  const setimgfile = (e) => {
    setFile(e.target.files[0]);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("language", language);
    formData.append("company_name", company_name);
    formData.append("cpoyright_text", cpoyright_text);
    formData.append("logo", file);


    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    await axios
      .post("http://localhost:5000/Add-generalsetings", formData, config)
      .then(() => {
        // Once posted, the user will be notified
        alert("Successfully Added The General Settings");
      });
  };

  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="/" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5 className="d-flex justify-content-center">
                    Welcome, {user && user.name}
                  </h5>
                </div>
                {/* <Link className="d-flex justify-content-center" onClick={logout} to="/" replace>
									<i className="fas fa-sign-out-alt"></i>{" "}
									<span className="hide-sm"> &nbsp;Logout</span>
								</Link>
								<a href="/" >Logout</a> */}
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">General Settings</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#/">General Settings Management</a>
                    </li>
                    <li className="breadcrumb-item active">General Settings</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <div></div>
          <section className="content"></section>
          <div className="container">
            <div className="row">
                <Form className="col-md-12 form-add p-5 shadow-lg rounded my-5">
                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Language</Form.Label>
                    <Form.Control
                      type="text"
                      name="language"
                      onChange={setLanguage}
                      placeholder="Language"
                    />
                  </Form.Group>

                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Copmpany Name</Form.Label>
                    <Form.Control
                      type="text"
                      name="company_name"
                      onChange={setCompanyName}
                      placeholder="Copmpany Name"
                    />
                  </Form.Group>

                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Copyright Text</Form.Label>
                    <Form.Control
                      type="text"
                      name="cpoyright_text"
                      onChange={setCopyrightText}
                      placeholder="Copyright Text"
                    />
                  </Form.Group>

                  <Form.Group className="" controlId="formBasicEmail">
                    <Form.Label>Site Logo</Form.Label>
                    <Form.Control
                      type="file"
                      name="logo"
                      onChange={setimgfile}
                      placeholder="Logo"
                    />
                  </Form.Group>

    

                  <Form.Group
                    className=""
                    controlId="formBasicEmail"
                    align="center"
                  >
                    <Button
                      variant="outline-primary"
                      type="submit"
                      onClick={addUserData}
                    >
                      Update
                    </Button>
                  </Form.Group>
                </Form>
            </div>
          </div>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
GeneralSettings.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(GeneralSettings);
