import React, { useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import { useHistory } from "react-router-dom";
import Footer from "./common/Footer";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const AddProducts = ({ auth: { user } }, isAuthenticated) => {
  const [products_name, setproducts_name] = useState("");
  const [products_short_desc, setproducts_short_desc] = useState("");
  const [taq, settaq] = useState("");
  const [maq, setmaq] = useState("");
  const [price, setprice] = useState("");
  const [sp_price, setsp_price] = useState("");
  const [products_sku, setproducts_sku] = useState("");
  const [products_sku_status, setproducts_sku_status] = useState("");
  const [products_big_desc, setproducts_big_desc] = useState("");

  const [file, setFile] = useState("");

  const history = useHistory();

  const setdata = (e) => {
    const { value } = e.target;
    setproducts_name(value);
    setproducts_short_desc(value);
    settaq(value);
    setmaq(value);
    setprice(value);
    setsp_price(value);
    setproducts_sku(value);
    setproducts_sku_status(value);
    setproducts_big_desc(value);
  };

  const setimgfile = (e) => {
    setFile(e.target.files[0]);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("products_name", products_name);
    formData.append("products_short_desc", products_short_desc);
    formData.append("taq", taq);
    formData.append("maq", maq);
    formData.append("products_img", file);
    formData.append("price", price);
    formData.append("sp_price", sp_price);
    formData.append("products_sku", products_sku);
    formData.append("products_sku_status", products_sku_status);
    formData.append("products_big_desc", products_big_desc);

    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    const res = await axios.post(
      "http://localhost:5000/Add-Products",
      formData,
      config
    );

    if (res.data.status === 401 || !res.data) {
      console.log("errror");
    } else {
      alert("Product Added Successfully")
    }
  };

  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Add Products</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <Form className="mt-3">
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="products_name"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Short Desc</Form.Label>
                  <Form.Control
                    type="text"
                    name="products_short_desc"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Total Allowed Quantity</Form.Label>
                  <Form.Control
                    type="text"
                    name="taq"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Minimum Order Quantity</Form.Label>
                  <Form.Control
                    type="text"
                    name="maq"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Product's Image</Form.Label>
                  <Form.Control
                    type="file"
                    onChange={setimgfile}
                    name="products_img"
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Price</Form.Label>
                  <Form.Control
                    type="text"
                    name="price"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Special Price</Form.Label>
                  <Form.Control
                    type="text"
                    name="sp_price"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Sku</Form.Label>
                  <Form.Control
                    type="text"
                    name="products_sku"
                    onChange={setdata}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products's SKU Status</Form.Label>
                  <Form.Select
                    name="products_sku_status"
                    className="form-control"
                    onChange={setdata}
                  >
                    <option value={"In tock"}>In tock</option>
                    <option value={"Out Of Stock"}>Out Of Stock</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Big Descriptions</Form.Label>
                  <textarea
                    name="products_big_desc"
                    className="form-control"
                    onChange={setdata}
                  />
                </Form.Group>

                <Form.Group
                  className="mb-3"
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button variant="primary" type="submit" onClick={addUserData}>
                    Submit
                  </Button>
                </Form.Group>
              </Form>
            </div>
          </div>
        </section>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
AddProducts.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(AddProducts);
