import Accordion from "react-bootstrap/Accordion";
import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Footer from "../common/Footer";
import Breadcrumb from "react-bootstrap/Breadcrumb";

const FaqComp = () => {
  const [Faq, setFaq] = useState("");

  useEffect(() => {
    const fetchFaq = async () => {
      const resp = await fetch("http://localhost:5000/Fetch-Faq");
      const json = await resp.json();
      if (resp.ok) {
        setFaq(json);
      }
    };

    fetchFaq();
  }, []);




  const [active, setActive] = useState(false);

  const contentRef = useRef(null);

  useEffect(() => {
    contentRef.current.style.maxHeight = active
      ? `${contentRef.current.scrollHeight}px`
      : "0px";
  }, [contentRef, active]);

  const toggleAccordion = () => {
    setActive(!active);
  };



  return (
    <section className="landing">
   
      
      <h4 className="text-light m-2">Frequently Asked Questions</h4>
      {/* Slider Part End */}
      <div className="container p-3">
        <div className="row">
          <div className="col-md-12">
            <h3 className="text-light" align="center">
            <button
            className={`question-section ${active}`}
            onClick={toggleAccordion}
          >
            <div>
              <div className="question-align">
                <h4 className="question-style">
                  Why do you like web developemnt
                </h4>
                <FiPlus
                  className={active ? `question-icon rotate` : `question-icon`}
                />
              </div>
              <div
                ref={contentRef}
                className={active ? `answer answer-divider` : `answer`}
              >
                <p>Because I love coding</p>
              </div>
            </div>
          </button>

            </h3>
          </div>
        </div>
      </div>
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />

      <Footer />
    </section>
  );
};

FaqComp.propTypes = {
  isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(FaqComp);
