import React, { useState } from "react";
import { connect } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import { register } from "../../actions/AdminAuth";
import PropTypes from "prop-types";
import Alert from "../layout/Alert.jsx";
import { setAlert } from "../../actions/alert";
import Footer from "../common/Footer";

const Register = ({ setAlert, register, isAuthenticated }) => {
	const [formData, setFormData] = useState({
		name: "",
		email: "",
		password: "",
		password2: "",
	});

	const { name, email, password, password2 } = formData;

	const onChange = (e) =>
		setFormData({ ...formData, [e.target.name]: e.target.value });

	const onSubmit = async (e) => {
		console.log("Form data", e);
		e.preventDefault();
		if (password !== password2) {
			setAlert("Password do not match", "danger");
		} else {
			register({ name, email, password });
		}
	};

	// Redirect if logged in
	// Redirect if logged in
	if (isAuthenticated) {
		return <Redirect to="/admin/dashboard" />
	}
	return (
		<>
		<div className="container">
      <div className="row">
        <div className="col-md-12 d-flex justify-content-center">
		<div className="register-form">
			<h1 className="heading" align="center">Sign Up</h1>
			<p className="lead" align="center">
				<i className="fas fa-user"></i> Create Your Account
			</p>
			<Alert />
			<br />
			<form className="form" onSubmit={(e) => onSubmit(e)}>
				<div className="form-group m-2">
					<input
						type="text"
						placeholder="Name"
						name="name"
						value={name}
						className="form-control"
						onChange={(e) => onChange(e)}
					/>
				</div>
				<div className="form-group m-2">
					<input
						type="email"
						placeholder="Email Address"
						name="email"
						value={email}
						className="form-control"
						onChange={(e) => onChange(e)}
					/>
				</div>
				<div className="form-group m-2">
					<input
						type="password"
						placeholder="Password"
						name="password"
						minLength="6"
						value={password}
						className="form-control"
						onChange={(e) => onChange(e)}
					/>
				</div>
				<div className="form-group m-2">
					<input
						type="password"
						placeholder="Confirm Password"
						name="password2"
						minLength="6"
						value={password2}
						className="form-control"
						onChange={(e) => onChange(e)}
					/>
				</div>
				<div className="form-group" align="center">
				<input type="submit" className="btn btn-outline-info" value="Register" />
				</div>
			</form>
			<div className="form-group">
			<p className="link">
				Already have an account? <Link className="text-decoration-none" to="/login">Sign In</Link>
			</p>
			</div>
		</div>
		</div>
		</div>
		</div>
		<div className="container">
        <div className="row">
          <div className="col-md-12">
         <Footer/>
          </div>
        </div>
		</div>
		</>
	);
};

Register.propTypes = {
	setAlert: PropTypes.func.isRequired,
	register: PropTypes.func.isRequired,
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { setAlert, register })(Register);
