import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";

import aboutusImg from "../../images_files/about-us.jpg"

const AbouUsComp = ({ isAuthenticated }) => {

    const [AbouUs, setAbouUs] = useState("");

    useEffect(() => {
      const fetchAbouUs = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-About");
        const json = await resp.json();
        if (resp.ok) {
          setAbouUs(json);
        }
      };
  
      fetchAbouUs();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
            <div className="col-md-12" align="center" >
             <img src={aboutusImg} alt={"About Us"} height={"300px"}/>
            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {AbouUs &&
                      AbouUs.map((aboutus) => (
                        <p
                         href=""
                          key={aboutus._id}
                        >
                          {aboutus.aboutUs}
                        </p>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
		</section>
	);
};

AbouUsComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(AbouUsComp);
