import React, { useState,useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import axios from "axios";
import AsideBar from "./common/AsideBar";
// import { Redirect } from "react-router-dom";
import Footer from "./common/Footer";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const AddProducts = ({ auth: { user } }) => {
  const [products_name, setproducts_name] = useState("");
  const [products_short_desc, setproducts_short_desc] = useState("");
  const [taq, settaq] = useState("");
  const [maq, setmaq] = useState("");
  const [price, setprice] = useState("");
  const [products_category, setproducts_category] = useState("");
  const [sp_price, setsp_price] = useState("");
  const [products_sku, setproducts_sku] = useState("");
  const [products_sku_status, setproducts_sku_status] = useState("");
  const [products_big_desc, setproducts_big_desc] = useState("");

  const [file, setFile] = useState("");

  // const history = useHistory();

  
  const setsetproducts_name = (e) => {
    const { value } = e.target;
    setproducts_name(value);
  };

