import Accordion from 'react-bootstrap/Accordion';
import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Footer from "../common/Footer";

import faqsImmg from "../../images_files/faqs-img.png"

const FaqComp = ({ isAuthenticated }) => {

    const [Faq, setFaq] = useState("");

    useEffect(() => {
      const fetchFaq = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-Faq");
        const json = await resp.json();
        if (resp.ok) {
          setFaq(json);
        }
      };
  
      fetchFaq();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
            <div className="col-md-12 m-0" align="center" >
             <img src={faqsImmg} alt={"About Us"} height={"300px"} width={"100%"}/>
            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {Faq &&
                      Faq.map((Faq) => (
                        <Accordion>
      <Accordion.Item eventKey="0">
        <Accordion.Header key={Faq._id}>{Faq.question}</Accordion.Header>
        <Accordion.Body key={Faq._id}>
        {Faq.answer}
        </Accordion.Body>
      </Accordion.Item>
      </Accordion>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
          <Footer/>
		</section>
	);
};

FaqComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(FaqComp);
