import React ,{useState,useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Carousel from "react-bootstrap/Carousel";
import img1 from "../../images_files/img1.jfif";
import img2 from "../../images_files/img2.jpg";
import img3 from "../../images_files/img3.jfif";
// import Breadcrumb from "react-bootstrap/Breadcrumb";
import Footer from "../common/Footer";
import Card from 'react-bootstrap/Card';

const Landing = ({ isAuthenticated }) => {


  const [Product, setProduct] = useState("");

  useEffect(() => {
    const fetchProduct = async () => {
      const resp = await fetch("http://localhost:5000/Fetch-Products");
      const json = await resp.json();
      if (resp.ok) {
        setProduct(json);
      }
    };

    fetchProduct();
  }, []);












  if (isAuthenticated) {
    return <Redirect to="/dashboard" />;
  }

  return (
    <section className="landing">
      <div className="container-fluid ">
        <div className="row">
          {/* <div className="col-md-12 d-flex justify-content-end">
          <Breadcrumb>
              <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
            </Breadcrumb>
          </div> */}
          <div className="col-md-12">

            <Carousel align="center">
              <Carousel.Item>
                <img className="d-block w-100" src={img1} alt="First slide" />
                <Carousel.Caption>
                  <h3>First slide label</h3>
                  <p>
                    Nulla vitae elit libero, a pharetra augue mollis interdum.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
              <Carousel.Item>
                <img className="d-block w-100" src={img2} alt="Second slide" />

                <Carousel.Caption>
                  <h3>Second slide label</h3>
                  <p>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
              <Carousel.Item>
                <img className="d-block w-100" src={img3} alt="Third slide" />

                <Carousel.Caption>
                  <h3>Third slide label</h3>
                  <p>
                    Praesent commodo cursus magna, vel scelerisque nisl
                    consectetur.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
            </Carousel>
          </div>
        </div>
      </div>
      {/* Slider Part End */}
      <div className="container p-3">
        <div className="row">
          <h3 className="" align="center">
            {" "}
            Welcome To <span className="text-danger ff">
              FLORIDA FASHION{" "}
            </span>{" "}
            Store
            <p align="center">
              Fashions Florida are online discount clothing store for brandname
              clothing was established in early 2022in South Florida. We procure
              sports and casual wear clothing for men, women and children from
              retail and wholesale channels at a deep discount so that we can
              pass on thesavings to our customers. We do not stock every other
              clothing with all sizes. We are in process of starting our own
              line of clothing soon. Please sign up with your email id/phone# to
              receive discounts and occasional email about sale items. We
              encourage resellers to signup for wholesale account to receive
              super low prices on brandname merchandise.
            </p>{" "}
          </h3>
          <div className="col-md-4" align="center">
            <div className="p-5">
              <i className="fa fa-truck p-5 d-4 bg-info"></i>
              <h3 className="m-0">FREE SHIPPING</h3>
              <p className="m-0">Free shipping on all orders over $199</p>
            </div>
          </div>
          <div className="col-md-4" align="center">
            <div className="p-5">
              <i className="fa fa-credit-card-alt d-4 p-5 bg-success"></i>
              <h3 className="m-0">MONEY BACK</h3>
              <p className="m-0">100% Money Back Garuntee</p>
            </div>
          </div>
          <div className="col-md-4" align="center">
            <div className="p-5">
              <i className="fa fa-question-circle d-4 p-5 bg-warning"></i>
              <h3 className="m-0">ONLINE SUPPORT</h3>
              <p className="m-0">Service Support Fast 24/7</p>
            </div>
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row">
          <div className="col-md-12"align="center">
              <h2 className="font-weight-bold text-info">Some Feteaured Products</h2>   
          </div>
        </div>
      </div>



                        <div className="container" align="center" style={{margin:"3rem"}}>
     <div className="row">
     {Product && Product.map((products) => (
     <div className="col-md-4" >
     <Card style={{ width: '18rem' }} >
     <Carousel align="center" className="bg-dark">
      <Carousel.Item>
        <img
          className="d-block w-100"
          src={`static/media/${products.products_img}`}
          alt="First slide"
        />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>

    </Carousel>
      <Card.Body className="bg-light" align="center">
        <Card.Title className="d-flex justify-content-center m-2">{products.products_name}</Card.Title>
        <Card.Text className="m-2">
          {products.products_short_desc}
        </Card.Text>
        <span className="m-2">{products.price}</span>
        <div align="center">
        <a href="/"  className="btn btn-outline-info m-2">Add To Cart</a>
        </div>
      </Card.Body>
    </Card>
     </div>
      ))}
     </div>

      </div>


      <Footer/>
    </section>
  );
};

Landing.propTypes = {
  isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(Landing);
