import React , {useEffect, useState} from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import {} from "../actions/auth";
import axios  from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
// import Table from "react-bootstrap/Table";
import DataTable from "react-data-table-component";

const ManageProducts = ({ auth: { user } }) => {

   const [productsData,setProductsData]=useState([]);
    
    const getProductsData= async()=>{
     try {
      const resp=await axios.get("http://localhost:5000/Fetch-Products");
      setProductsData(resp.data);
     } catch (error) {
      console.log(error);
     }
    }

   const Collumns=[
    {
      name:"Product's Name",
      selector:row=>row.products_name
    },
    {
      name:"Product's Short Description",
      selector:row=>row.products_short_desc
    },
    {
      name:"Total Allowed Quantity",
      selector:row=>row.taq
    },
    {
      name:"Minimum Order Quantity",
      selector:row=>row.maq
    },
    {
      name:"Product's Image",
      selector:row=><img width={70} height={70} src={`../uploads/${row.products_img}`} alt={"Florida Fashion"} />
    },
    {
      name:"Product's Price",
      selector:row=>row.price
    },
    {
      name:"Product's Special Price",
      selector:row=>row.sp_price
    },
    {
      name:"Product's SKU",
      selector:row=>row.products_sku
    },
    {
      name:"Product's SKU Status",
      selector:row=>row.products_sku_status
    },
    {
      name:"Product's Big Description",
      selector:row=>row.products_big_desc
      
    },
    {
      name:"Edit",
      cell:(row)=><a href={`edit/${row._id}`} className="btn btn-success">Edit</a>
            
    },
    {
      name:"Delete",
      cell:(row)=><button className="btn btn-danger" >Delete</button>
            
    },  
   ]
     
    useEffect(()=>{
      getProductsData();
    }, []);
  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Manage Products</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-12">
                {/* <Table striped bordered hover>
                  <thead>
                    <tr>
                      <th>Products' Name</th>
                      <th>Products' Category</th>
                      <th>Products' Price</th>
                      <th>Products' Special Price</th>
                      <th>Products' Image</th>
                      <th>Products' Description</th>
                      <th>Products' Stock Status</th>
                    </tr>
                  </thead>
                  <tbody></tbody>
                </Table> */}

                <DataTable 
                title="Products' Data"
                columns={Collumns}
                data={productsData}
                pagination
                fixedHeader
                selectableRows
                />
              </div>
            </div>
          </div>
        </section>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
ManageProducts.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(ManageProducts);
