  const [products_short_desc, setproducts_short_desc] = useState("");
  const [taq, settaq] = useState("");
  const [maq, setmaq] = useState("");
  const [price, setprice] = useState("");
  const [products_category, setproducts_category] = useState("");
  const [products_sub_category, setProducts_sub_category] = useState("");
  const [brands_name, setbrands_name] = useState("");
  const [sp_price, setsp_price] = useState("");

  const [file, setFile] = useState("");

  // const history = useHistory();

  
  const setsetproducts_name = (e) => {
    const { value } = e.target;
    setproducts_name(value);
  };

  const setShortDesc = (e) => {
    const { value } = e.target;
    setproducts_short_desc(value);
  };

   const settotalAllowedQuantity=(e)=>{
    const { value } = e.target;
    settaq(value);
   }

   const setMinimumOrderQuantity=(e)=>{
    const { value } = e.target;
    setmaq(value);

   }
   const setPrice=(e)=>{
    const { value } = e.target;
    setprice(value);

   }
   const setSpPrice=(e)=>{
    const { value } = e.target;
    setsp_price(value);

   }
   const setproductscategory=(e)=>{
    const { value } = e.target;
      setproducts_category(value)

   }

   const setProductsSsubCcategory=(e)=>{
    const { value } = e.target;
    setProducts_sub_category(value)

   }

   const SetBrandsNAME=(e)=>{
    const { value } = e.target;
    setbrands_name(value);

   }

  



  const setimgfile = (e) => {
    setFile(e.target.files[0]);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("products_name", products_name);
    formData.append("products_short_desc", products_short_desc);
    formData.append("taq", taq);
    formData.append("maq", maq);
    formData.append("products_img", file);
    formData.append("products_category", products_category);
    formData.append("products_sub_category", products_sub_category);
    formData.append("brands_name", brands_name);
    formData.append("price", price);
    formData.append("sp_price", sp_price);
    

    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    await axios.post("http://localhost:5000/Add-Products", formData, config).then(()=>alert("Product Added")).catch((err)=>alert(err));
  };



  const [SubCategory, setSubCategory] = useState("");
   
  useEffect(()=>{
   const fetchSubCategory=async()=>{
      const resp=await fetch("http://localhost:5000/Fetch-SubCategories");
      const json=await resp.json()
      if(resp.ok){
        setSubCategory(json);
      }
   }

   fetchSubCategory()
 }, [])


   const [categoryNames, setcategoryNames] = useState("");
   
    useEffect(()=>{
     const fetchCatecoryName=async()=>{
        const resp=await fetch("http://localhost:5000/Fetch-Categories");
        const json=await resp.json()
        if(resp.ok){
          setcategoryNames(json);
        }
     }

     fetchCatecoryName()
   }, [])

   const [BrandsName, setBrandsName] = useState("");
   
    useEffect(()=>{
     const fetchBrandsName=async()=>{
        const resp=await fetch("http://localhost:5000/Fetch-Brands");
        const json=await resp.json()
        if(resp.ok){
          setBrandsName(json);
        }
     }

     fetchBrandsName()
   }, [])
  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Add Products</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <Form className="mt-3">
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="products_name"
                    onChange={setsetproducts_name}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Short Desc</Form.Label>
                  <Form.Control
                    type="text"
                    name="products_short_desc"
                    onChange={setShortDesc}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Total Allowed Quantity</Form.Label>
                  <Form.Control
                    type="text"
                    name="taq"
                    onChange={settotalAllowedQuantity}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Minimum Order Quantity</Form.Label>
                  <Form.Control
                    type="text"
                    name="maq"
                    onChange={setMinimumOrderQuantity}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Product's Image</Form.Label>
                  <Form.Control
                    type="file"
                    onChange={setimgfile}
                    name="products_img"
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products's Category</Form.Label>
                  <Form.Select
                    name="products_category"
                    className="form-control"
                    onChange={setproductscategory}
                  >
                    <option>---------</option>

                    {/* <option value={"1"}>1</option>
                    <option value={"2"}>2</option> */}
                    {categoryNames && categoryNames.map((category)=>(
                      <option key={category._id} value={category.category_name}>{category.category_name}</option>
                    ))}
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products's Sub Category</Form.Label>
                  <Form.Select
                    name="products_sub_category"
                    className="form-control"
                    onChange={setProductsSsubCcategory}
                  >
                    <option>---------</option>
                    {/* <option value={"1"}>1</option>
                    <option value={"2"}>2</option> */}
                    {SubCategory && SubCategory.map((Subcategory)=>(
                      <option key={Subcategory._id} value={Subcategory.sub_category_name}>{Subcategory.sub_category_name}</option>
                    ))}
                  </Form.Select>
                </Form.Group>

   
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products's Brands Name</Form.Label>
                  <Form.Select
                    name="brands_name"
                    className="form-control"
                    onChange={SetBrandsNAME}
                  >
                    <option>---------</option>

                    {/* <option value={"1"}>1</option>
                    <option value={"2"}>2</option> */}
                    {BrandsName && BrandsName.map((brands)=>(
                      <option key={brands._id} value={brands.brands_name}>{brands.brands_name}</option>
                    ))}
                  </Form.Select>
                </Form.Group>


                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Price</Form.Label>
                  <Form.Control
                    type="text"
                    name="price"
                    onChange={setPrice}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Special Price</Form.Label>
                  <Form.Control
                    type="text"
                    name="sp_price"
                    onChange={setSpPrice}
                    placeholder=""
                  />
                </Form.Group>

                
                <Form.Group
                  className="mb-3"
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button variant="primary" type="submit" onClick={addUserData}>
                    Submit
                  </Button>
                </Form.Group>
              </Form>
            </div>
          </div>
        </section>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
AddProducts.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(AddProducts);
