                    name="maq"
                    onChange={(event) => {
                      setmaq(event.target.value);
                    }}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Product's Image</Form.Label>
                  <Form.Control
                    type="file"
                    onChange={(event) => {
                      setproducts_img(event.target.files[0]);
                    }}
                    name="products_img"
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
