                    onChange={(event) => {
                      setproducts_sku(event.target.value);
                    }}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products's SKU Status</Form.Label>
                  <Form.Select
                    name="products_sku_status"
                    className="form-control"
                    onChange={(event) => {
                      setproducts_sku_status(event.target.value);
                    }}
                  >
                    <option value={"In tock"}>In tock</option>
                    <option value={"Out Of Stock"}>Out Of Stock</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Products Big Descriptions</Form.Label>
                  <textarea
                    name="products_big_desc"
                    className="form-control"
                    onChange={(event) => {
                      setproducts_big_desc(event.target.value);
                    }}
                  />
                </Form.Group>

                <Form.Group
                  className="mb-3"
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button
                    variant="outline-primary"
                    type="submit"
                    onClick={addProductsData}
                  >
                    Add
                  </Button>
                </Form.Group>
              </Form> */}
              <form>
                <input type="text" name="products_name" />
                <input type="text" name="products_short_desc" />
                <input type="number" name="taq" />
                <input type="number" name="maq" />
                <input type="file" name="products_img" />
                <input type="number" name="price" />
