import React, { useState } from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { login } from "../../actions/auth";
import Alert from "../layout/Alert.jsx";
import Footer from "../common/Footer";
const Login = ({ login, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();

    login(email, password);
  };

  // Redirect if logged in
	if (isAuthenticated && email ==="Admin@gmail.com") {
		return <Redirect to="/admin/dashboard" />
	}
  else{window.location.href ="/user/dashboard"}

  return (
    <>
        <div className="container">
      <div className="row">
        <div className="col-md-12 d-flex justify-content-center">
          <div className="login-form" style={{marginTop:"120px",color:"whitesmoke"}}>
            <h1 className="heading" align="center">
              Sign In
            </h1>
            <p className="lead" align="center">
              <i className="fas fa-user"></i> Sign Into Your Account
            </p>
            <Alert />
            <br />
            <form className="form" onSubmit={(e) => onSubmit(e)}>
              <div className="form-group m-2">
                <input
                  type="email"
                  placeholder="Email Address"
                  name="email"
                  value={email}
                  className="form-control"
                  onChange={(e) => onChange(e)}
                  required
                />
              </div>
              <div className="form-group m-2">
                <input
                  type="password"
                  placeholder="Password"
                  name="password"
                  minLength="6"
                  value={password}
                  className="form-control"
                  onChange={(e) => onChange(e)}
                  required
                />
              </div>
              <div className="form-group m-2" align="center">
                <input
                  type="submit"
                  className="btn btn-outline-info"
                  value="Login"
                />
              </div>
            </form>
            <p className="link">
              Don't have an account?{" "}
              <Link className="" to="/register">
                Sign Up
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
    <div>
      <div className="container">
        <div className="row">
          <div className="col-md-12">
         <Footer/>
          </div>
        </div>
      </div>
    </div>
    </>
  );
};

Login.propTypes = {
  login: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { login })(Login);
