import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
// import icon from "../img/user.png";
import {} from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
import ReactQuill from "react-quill";
import EditorToolbar, { modules, formats } from "./common/EditorToolbar";
import "react-quill/dist/quill.snow.css";

const AdminPolicy=({ auth: { user } }) => {
  

  let history = useHistory();
  const [userInfo, setuserInfo] = useState({
    adminPolicy: "",
  });
 
  const onadminPolicy = (value) => {
    setuserInfo({ ...userInfo, adminPolicy: value });
  };
 
  const [isError] = useState(null);
  const addDetails = async (event) => {
    try {
      event.preventDefault();
      event.persist();

      axios
        .post(`http://localhost:5000/AdminPolicy`, {
          adminPolicy: userInfo.adminPolicy,
        })
        .then((res) => {
          if (res.data.success === true) {
            history.push("/");
          }
        });
    } catch (error) {
      throw error;
    }
  };

  
    return (
      <div className="wrapper">
        <div>
          <nav className="main-header navbar navbar-expand navbar-white navbar-light">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a
                  className="nav-link"
                  data-widget="pushmenu"
                  href="#/"
                  role="button"
                >
                  <i className="fas fa-bars" />
                </a>
              </li>
              <li className="nav-item d-none d-sm-inline-block">
                <a href="index3.html" className="nav-link">
                  Home
                </a>
              </li>
              <li className="nav-item d-none d-sm-inline-block"></li>
            </ul>
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <a
                  className="nav-link"
                  data-widget="navbar-search"
                  href="#/"
                  role="button"
                >
                  <i className="fas fa-search" />
                </a>
                <div className="navbar-search-block">
                  <form className="form-inline">
                    <div className="input-group input-group-sm">
                      <input
                        className="form-control form-control-navbar"
                        type="search"
                        placeholder="Search"
                        aria-label="Search"
                      />
                      <div className="input-group-append">
                        <button className="btn btn-navbar" type="submit">
                          <i className="fas fa-search" />
                        </button>
                        <button
                          className="btn btn-navbar"
                          type="button"
                          data-widget="navbar-search"
                        >
                          <i className="fas fa-times" />
                        </button>
                      </div>
                    </div>
                  </form>
                </div>
              </li>

              <li className="nav-item dropdown">
                <a className="nav-link" data-toggle="dropdown" href="#/">
                  <i className="fa fa-user" />
                </a>
                <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                  <div style={{ marginTop: "", textAlign: "center" }}>
                    <h5>Welcome, {user && user.name}</h5>
                  </div>
                </div>
              </li>
            </ul>
          </nav>
        </div>
        <div>
          <AsideBar />
        </div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">Admin Policy</h1>
                </div>
              </div>
            </div>
          </div>
          <section className="content">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">
                  <div className="editor">
                    <div className="col-md-12">
                    <form onSubmit={addDetails} className="update__forms">
                  <h3 className="myaccount-content"> Add </h3>
                  <div className="form-row">
                    
                    <div className="clearfix"></div>
                    <div className="form-group col-md-12 editor">
                      <label className="font-weight-bold">
                        {" "}
                        Admin Policy <span className="required"> * </span>{" "}
                      </label>
                      <EditorToolbar toolbarId={"t1"} />
                      <ReactQuill
                        theme="snow"
                        value={userInfo.adminPolicy}
                        onChange={onadminPolicy}
                        placeholder={"Write something awesome..."}
                        modules={modules("t1")}
                        formats={formats}
                      />
                    </div>                    
                    <br />
                    {isError !== null && (
                      <div className="errors"> {isError} </div>
                    )}
                    <div className="form-group col-sm-12 " align="center">
                      <button type="submit" className="btn btn-outline-primary btn__theme">
                        {" "}
                        Submit{" "}
                      </button>
                    </div>
                  </div>
                </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        </div>
        <div>
          <Footer />
        </div>
      </div>
    );
  }

AdminPolicy.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(AdminPolicy);
