import React, { useState } from "react";
import {  Redirect,Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { loginusers } from "../../actions/auth2";
import Alert2 from "../layout/Alert2";
import Footer from "../common/Footer";
const UserLogin = ({ loginusers, isAuthenticated2 }) => {
	const [formData, setFormData] = useState({
		email: "",
		password: "",
	});

	const { email, pass } = formData;

	const onChange = (e) =>
		setFormData({ ...formData, [e.target.name]: e.target.value });

	const onSubmit = async (e) => {
		e.preventDefault();

		loginusers(email, pass);
	};

	// Redirect if logged in
	if (isAuthenticated2) {
		return <Redirect to="/user/dashboard" />;
	}

	return (
		<div className="login-form">
			<div className="text-light" align="center"> 
			<h1 className="heading">Sign In</h1>
			<p className="lead">
				<i className="fas fa-user"></i> Sign Into Your Account
			</p>
			</div>

			<Alert2 />
			<br />
			<div className="container">
				<div className="row d-flex justify-content-center">
					<div className="col-md-6 bg-light p-5" style={{borderRadius:"19px"}}>
					<form className="form" onSubmit={(e) => onSubmit(e)}>
				<div className="form-group">
				
					<input
						type="email"
						placeholder="Email Address"
						name="email"
						value={email}
						onChange={(e) => onChange(e)}
						className="form-control w-100"
						required
					/>
					
				</div>
				<div className="form-group">
				
					<input
						type="password"
						placeholder="Password"
						name="pass"
						minLength="6"
						value={pass}
						onChange={(e) => onChange(e)}
						className="form-control w-100"
						required
					/>
				</div>
				<input type="submit" className="btn btn-outline-info w-100" value="Sign In" />
			</form>
			<p className="link">
				Don't have an account? <Link to="/user/register">Sign Up</Link>
			</p>
			
					</div>
				</div>

			</div>
			<Footer/>
		</div>
	);
};

UserLogin.propTypes = {
	login: PropTypes.func.isRequired,
	isAuthenticated2: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated2: state.auth.isAuthenticated2,
});

export default connect(mapStateToProps, { loginusers })(UserLogin);
