import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import {} from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
import DataTable from "react-data-table-component";

const ManageProducts = ({ auth: { user } }) => {
  const [productsData, setProductsData] = useState([]);

  const getProductsData = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/usersFetching");
      setProductsData(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  const Collumns = [
    {
      name: "First Name",
      selector: (row) => row.fname,
    },
    {
      name: "Last Name",
      selector: (row) => row.lname,
    },
    {
      name: "Email Address",
      selector: (row) => row.email,
    },
    {
      name: "Phone",
      selector: (row) => row.phn,
    },
    
    {
      name: "Password",
      selector: (row) => row.pass,
    },

    {
      name: "Role",
      selector: (row) => row.role,
    },
    {
      name: "Status",
      selector: (row) => row.status,
    },
    {
      name: "Email Status",
      selector: (row) => row.EmailPending,
    },
    {
      name: "View",
      cell: (row) => (
        <a href={`/edit?id=${row._id}`} className="btn btn-info d-inline">
          <i className="fa fa-eye d-inline"></i>View
        </a>
      ),
    },
    {
      name: "Edit",
      cell: (row) => (
        <a href={`/edit?id=${row._id}`} className="btn btn-success">
          <i className="fa fa-pencil"></i>Edit
        </a>
      ),
    },
    {
      name: "Delete",
      cell: (row) => (
        <button className="btn btn-danger">
          <i className="fa fa-trash"></i>Delete
        </button>
      ),
    },
  ];

  useEffect(() => {
    getProductsData();
  }, []);
  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Manage Products</h1>
              </div>
            </div>
          </div>
        </div>
        <div className="container">
          <div className="row p-3">
          <div className="col-md-12">
                  <ul className="list-unstyled" align="center">
                    <li className="list-inline-item p-2 btn btn-primary">
                      {" "}
                      <i className="fa fa-user d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/users">
                        All Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-success">
                      {" "}
                      <i className="fa fa-user-plus d-inline"></i>{" "}
                      <a className="text-dark" href="/add-users">
                        Add Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-warning">
                      {" "}
                      <i className="fa fa-ban d-inline"></i>{" "}
                      <a className="text-dark" href="/suspend-users">
                        Suspended Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-danger">
                      {" "}
                      <i className="fas fa-user-times d-inline"></i>{" "}
                      <a className="text-dark" href="/delete-users">
                        Deleted Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-info">
                      {" "}
                      <i className="fa fa-envelope"></i>{" "}
                      <a className="text-dark" href="/email-pending">
                        Pendin Emails
                      </a>
                    </li>
                  </ul>
                </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-12">
                <DataTable
                  title="Products' Data"
                  columns={Collumns}
                  data={productsData}
                  pagination
                  fixedHeader
                  selectableRows
                />
              </div>
            </div>
          </div>
        </section>
      </div>
      <div>
