import React, { useState,useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
// import icon from "../img/user.png";
import {} from "../actions/auth";
import axios  from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";


const AddSubCategory = ({ auth: { user } }) => {
  const [sub_category_name, setSubcategory_name] = useState("");
  const [sub_category_title, setSubcategory_title] = useState("");
  const [parent_category, setParent_category] = useState("");
  const [sub_category_desc, setSubcategory_desc] = useState("");

  

  const [file, setFile] = useState("");

  // const history = useHistory();

  const setSubcategoryname = (e) => {
    const { value } = e.target;
    setSubcategory_name(value);
    
  };

  const setSubcategorytitle = (e) => {
    const { value } = e.target;
    setSubcategory_title(value);
  };

  const setParentcategory = (e) => {
    const { value } = e.target;
    setParent_category(value);
  };

  const setSubcategorydesc = (e) => {
    const { value } = e.target;
    setSubcategory_desc(value);
  };



  const setimgfile = (e) => {
    setFile(e.target.files[0]);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("sub_category_name", sub_category_name);
    formData.append("sub_category_image", file);
    formData.append("sub_category_title", sub_category_title);
    formData.append("parent_category", parent_category);
    formData.append("sub_category_desc", sub_category_desc);

    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

      await axios.post(
      "http://localhost:5000/Add-SubCategories",
      formData,
      config
    ).then(() => {
      // Once posted, the user will be notified
      alert("Successfully Added The Sub Category");
    });
  };

  const [categoryNames, setcategoryNames] = useState("");
   
  useEffect(()=>{
   const fetchCatecoryName=async()=>{
      const resp=await fetch("http://localhost:5000/Fetch-Categories");
      const json=await resp.json()
      if(resp.ok){
        setcategoryNames(json);
      }
   }

   fetchCatecoryName()
 }, [])





  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
       <AsideBar/>
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Add Sub Category</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
            <Form className="mt-3">
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Subcategory Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="sub_category_name"
                    onChange={setSubcategoryname}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Subcategory Image</Form.Label>
                  <Form.Control
                    type="file"
                    name="sub_category_image"
                    onChange={setimgfile}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Parent Category</Form.Label>
                <Form.Select
                  name="parent_category"
                  className="form-control"
                  onChange={setParentcategory}
                >
                  <option>-----------</option>
                  {categoryNames && categoryNames.map((category)=>(
                      <option key={category._id} value={category.category_name}>{category.category_name}</option>
                    ))}
                </Form.Select>
              </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Subcategory Title</Form.Label>
                  <Form.Control
                    type="text"
                    name="sub_category_title"
                    onChange={setSubcategorytitle}
                    placeholder=""
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Subcategory Descriptions</Form.Label>
                  <Form.Control
                    type="text"
                    name="sub_category_desc"
                    onChange={setSubcategorydesc}
                    placeholder=""
                  />
                </Form.Group>   

                <Form.Group
                  className="mb-3"
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button variant="primary" type="submit" onClick={addUserData}>
                    Submit
                  </Button>
                </Form.Group>             
              </Form>
            </div>
          </div>
        </section>
      </div>
      <div>
       <Footer/>
      </div>
    </div>
  );
};
AddSubCategory.propTypes = {
  auth: PropTypes.object.isRequired,
};

