import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Carousel from "react-bootstrap/Carousel";
import img1 from "../../images_files/img1.jfif";
import img2 from "../../images_files/img2.jpg";
import img3 from "../../images_files/img3.jfif";
const AbouUsComp = ({ isAuthenticated }) => {

    const [AbouUs, setAbouUs] = useState("");

    useEffect(() => {
      const fetchAbouUs = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-About");
        const json = await resp.json();
        if (resp.ok) {
          setAbouUs(json);
        }
      };
  
      fetchAbouUs();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
            <div className="col-md-12">
              <Carousel align="center">
                <Carousel.Item>
                  <img className="d-block w-100" src={img1} alt="First slide" />
                  <Carousel.Caption>
                    <h3>First slide label</h3>
                    <p>
                      Nulla vitae elit libero, a pharetra augue mollis interdum.
                    </p>
                  </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                  <img
                    className="d-block w-100"
                    src={img2}
                    alt="Second slide"
                  />

                  <Carousel.Caption>
                    <h3>Second slide label</h3>
                    <p>
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                    </p>
                  </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                  <img className="d-block w-100" src={img3} alt="Third slide" />

                  <Carousel.Caption>
                    <h3>Third slide label</h3>
                    <p>
                      Praesent commodo cursus magna, vel scelerisque nisl
                      consectetur.
                    </p>
                  </Carousel.Caption>
                </Carousel.Item>
              </Carousel>
            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {AbouUs &&
                      AbouUs.map((aboutus) => (
                        <p
                         href=""
                          key={aboutus._id}
                        >
                          {aboutus.aboutUs}
                        </p>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
		</section>
	);
};

AbouUsComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(AbouUsComp);
