import React, { useState,useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
// import icon from "../img/user.png";
import {} from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";

import ReactQuill from "react-quill";
import EditorToolbar, { modules, formats } from "./common/EditorToolbar";
import "react-quill/dist/quill.snow.css";
import Footer from './common/Footer';

const AdminPolicy=({ auth: { user } }) => {
  

  const [userInfo, setuserInfo] = useState({
    adminPolicy: "",
  });
 
  const onadminPolicy = (value) => {
    setuserInfo({ ...userInfo, adminPolicy: value });
  };
 
  useEffect(() => {
    const fetchAdminPolicy = async () => {
      const resp = await fetch("http://localhost:5000/Fetch-AdminPolicy");
      const json = await resp.json();
      if (resp.ok) {
        setuserInfo(json);
      }
    };

    fetchAdminPolicy();
  }, []);


  const [isError] = useState(null);
  const addDetails = async (event) => {
    try {
      event.preventDefault();
      event.persist();

      axios
        .post(`http://localhost:5000/AdminPolicy`, {
          adminPolicy: userInfo.adminPolicy,
        })
        .then((data)=>{console.log(data,"Admin Policy Data Has Been Updated Successfully")
      
      if (data.status === 200) {
        alert(`Admin Policy Data Has Been Updated Successfully`);
        window.location.href = "/admin/aboutus";
      }
    
    })
    } catch (error) {
      throw error;
    }
  };


  function calcTime(city,offset){
		var b=new Date()
		var utc=b.getTime()+(b.getTimezoneOffset()*60000);
		var nd=new Date(utc+(3600000*offset));
		return nd.getHours();

	}

  

  var showime=(calcTime('Los Angeles','-8'));
  
  
    return (
      <div className="wrapper">
        <div>
          <nav className="main-header navbar navbar-expand navbar-white navbar-light">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a
                  className="nav-link"
                  data-widget="pushmenu"
                  href="#/"
                  role="button"
                >
                  <i className="fas fa-bars" />
                </a>
              </li>
              <li className="nav-item d-none d-sm-inline-block">
                <a href="index3.html" className="nav-link">
                  Home
                </a>
              </li>
              <li className="nav-item d-none d-sm-inline-block"></li>
            </ul>
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <a
                  className="nav-link"
                  data-widget="navbar-search"
                  href="#/"
                  role="button"
                >
                  <i className="fas fa-search" />
                </a>
                <div className="navbar-search-block">
                  <form className="form-inline">
                    <div className="input-group input-group-sm">
                      <input
                        className="form-control form-control-navbar"
                        type="search"
                        placeholder="Search"
                        aria-label="Search"
                      />
                      <div className="input-group-append">
                        <button className="btn btn-navbar" type="submit">
                          <i className="fas fa-search" />
                        </button>
                        <button
                          className="btn btn-navbar"
                          type="button"
                          data-widget="navbar-search"
                        >
                          <i className="fas fa-times" />
                        </button>
                      </div>
                    </div>
                  </form>
                </div>
              </li>

              <li className="nav-item dropdown">
                <a className="nav-link" data-toggle="dropdown" href="#/">
                  <i className="fa fa-user" />
                </a>
                <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                  <div style={{ marginTop: "", textAlign: "center" }}>
                    <h5>Welcome, {user && user.name}</h5>
                  </div>
                </div>
              </li>
            </ul>
          </nav>
        </div>
        <div>
          <AsideBar />
        </div>
        <div className="content-wrapper">
        <h5 className="text-dark text-center font-weight-bold">Welcome, {user && user.name} ðŸ‘‹</h5>
          <h5 className="text-dark text-center font-weight-bold">
          {(() => {
        if (showime>=3 && showime<12) {
          return <div> Good Morning <i className="fa fa-sun text-warning"></i></div>;
        } else if(showime>=12 && showime<18){
          return <div> Good Afternoon <i className="fa fa-sun text-warning "></i> </div>;
        } 

        else if(showime>=18 && showime<23){
          return <div> Good Evening <i className="fa fa-moon text-dark"></i></div>;
        } 

         else{
          return <div> Good Evening <i className="fa fa-moon text-dark"></i></div>;

         }
        
      })()}
          </h5>
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">Admin Policy</h1>
                </div>
              </div>
            </div>
          </div>
          <section className="content">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">
                  <div className="editor">
                    <div className="col-md-12">
                    <form onSubmit={addDetails} className="update__forms">
                  <h3 className="myaccount-content"> Add </h3>
                  <div className="form-row">
                    
                    <div className="clearfix"></div>
                    <div className="form-group col-md-12 editor">
                      <label className="font-weight-bold">
                        {" "}
                        Admin Policy <span className="required"> * </span>{" "}
                      </label>
                      <EditorToolbar toolbarId={"t1"} />
                      <ReactQuill
                        theme="snow"
                        value={userInfo.adminPolicy}
                        onChange={onadminPolicy}
                        placeholder={"Write something awesome..."}
                        modules={modules("t1")}
                        formats={formats}
                      />
                    </div>                    
                    <br />
                    {isError !== null && (
                      <div className="errors"> {isError} </div>
                    )}
                    <div className="form-group col-sm-12 " align="center">
                      <button type="submit" className="btn btn-outline-primary btn__theme">
                        {" "}
                        Save{" "}
                      </button>
                    </div>
                  </div>
                </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
