import React, { useEffect,useState,createContext } from "react";
import ReactSwitch from "react-switch";


// import "./App.css";
import "./css/custom.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navbar from "./components/layout/Navbar.jsx";
import Landing from "./components/layout/Landing.jsx";
import Login from "./components/auth/Login.jsx";
import Register from "./components/auth/Register.jsx";
import PrivateRoute from "./routing/PrivateRoute.jsx";
import PrivateRoute2 from "./routing/PrivateRoute2.jsx";
import ManageProducts from "./components/ManageProducts.jsx";
import PrivateRoutingManageProducts from "./routing/PrivateRoutingManageProducts.jsx";
import AddCategory from "./components/AddCategory.jsx";
import PrivateRoutingAddCategory from "./routing/PrivateRoutingAddCategory.jsx";
import ManageCategory from "./components/ManageCategory.jsx";
import PrivateRoutingManageCategories from "./routing/PrivateRoutingManageCategories.jsx";
import AboutUs from "./components/AboutUs.jsx";
import PrivateRoutingAboutUs from "./routing/PrivateRoutingAboutUs.jsx";	
import Dashboard from "./components/Dashboard.jsx";
import contactus from "./components/contactus.jsx";
import PrivateRoutingContactUs from "./routing/PrivateRoutingContactUs.jsx";
import PrivacyPolicy from "./components/PrivacyPolicy.jsx";
import PrivateRoutingPrivacyPolicy from "./routing/PrivateRoutingPrivacyPolicy.jsx";
import PivateRoutingAdminPolicy from "./routing/PivateRoutingAdminPolicy.jsx";
import Adminpolicy from './components/AdminPolicy.jsx';
import PrivateRoutingFaq from "./routing/PrivateRoutingFaqs.jsx";
import Faqs from './components/Faq.jsx';
import TnC from './components/TnC.jsx';
import PrivateRoutingTnC from './components/TnC.jsx';
import AddProducts from './components/AddProducts.jsx'
import userDashboard from "./components/user-dashboard.jsx";
import PrivateRouteUserDashboard from "./routing/PrivateRouteUserDashboard.jsx";
import addBrands from "./components/add-brands.jsx";
import PrivateRouteAddBrands from "./routing/PrivateRouteAddBrands.jsx";
import manageBrands from "./components/manage-brands.jsx";
import PrivateRoutingManageBrands from "./routing/PrivateRoutingManageBrands.jsx";
import aboutUs from "./components/layout/aboutUs.jsx";
import adminPolicy from "./components/layout/adminPolicy.jsx";
import privacyPolicy from "./components/layout/privacyPolicy.jsx";
import contactUs from "./components/layout/contactUs.jsx";
import FAQs from "./components/layout/FAQs.jsx";
import tnc from "./components/layout/tnc.jsx";
import edit_products from "./components/edit_products.jsx";
import PrivateRoutingEditProducts from "./routing/PrivateRoutingEditProducts.jsx";
import newsManagement from "./components/newsManagement";
import PrivateRoutingNewsManagement from "./routing/PrivateRoutingNewsManagement";
import addNews from "./components/addNews";
import PrivateRoutingAddNews from "./routing/PrivateRoutingAddNews";







//Auth//
import setAuthToken from "./utils/setAuthToken";
import store from "./store";
import { loadUser } from "./actions/auth";
import NotFound from "./components/NotFound.jsx";

export const ThemeContext = createContext("light")



if (localStorage.token) {
	setAuthToken(localStorage.token);
}

function App() {
	useEffect(() => {
		store.dispatch(loadUser());
	}, []);


	useEffect(() => {
		store.dispatch(loadUser());
	}, []);
	const [theme, setTheme] = useState("light");
	const toggleTheme=()=>{
		setTheme((curr)=>(curr==="light" ? "dark":"light"))
	}

	return (
		<>
		<ThemeContext.Provider value={{theme,toggleTheme}} >
			<div className="App" id={theme}>
				<Router>
					<Navbar />
					<Switch>
						<Route exact path="/" component={Landing} />
						<PrivateRoute exact path="/dashboard" component={Dashboard} />
						<PrivateRoute2 exact path="/add-products" component={AddProducts} />
						<PrivateRoutingManageProducts exact path="/manage-products" component={ManageProducts} />
						<PrivateRoutingAddCategory exact path="/add-category" component={AddCategory} />
						<PrivateRoutingManageCategories exact path="/manage-category" component={ManageCategory} />
						<PrivateRoutingAboutUs exact path="/aboutus" component={AboutUs} />
						<PrivateRoutingContactUs exact path="/contactus" component={contactus} />
						<PrivateRoutingPrivacyPolicy exact path="/privaypolicy" component={PrivacyPolicy} />
						<PivateRoutingAdminPolicy exact path="/admin-policy" component={Adminpolicy} />
						<PrivateRoutingFaq exact path="/faqs" component={Faqs} />
						<PrivateRoutingTnC exact path="/tnc" component={TnC} />
						<PrivateRouteUserDashboard exact path="/user-dashboard" component={userDashboard} />
						<PrivateRouteAddBrands exact path="/add-brands" component={addBrands} />
						<PrivateRoutingManageBrands exact path="/manage-brands" component={manageBrands} />
						<PrivateRoutingEditProducts exact path="/edit" component={edit_products} />
						<PrivateRoutingNewsManagement exact path="/admin/news-list" component={newsManagement} />
						<PrivateRoutingAddNews exact path="/add-news" component={addNews} />








						<Route exact path="/register" component={Register} />
						<Route exact path="/login" component={Login} />
						<Route exact path="/about" component={aboutUs} />
						<Route exact path="/adminPolicy" component={adminPolicy} />
						<Route exact path="/privacy" component={privacyPolicy} />
						<Route exact path="/contact" component={contactUs} />
						<Route exact path="/faq" component={FAQs} />
						<Route exact path="/t&c" component={tnc} />
						<Route exact path="" component={NotFound} />
					</Switch>
				</Router>
				<div className="toogle-theme">
				<h4 className="text-info font-weight-bold d-flex justify-content-center">Toggle Theme</h4>
			    <div className="m-2 d-flex justify-content-center">
				<label className="text-theme font-weight-bold">{theme==="light" ? "Light Mode" :"Dark Mode"}</label> &nbsp;&nbsp;
			<ReactSwitch onChange={toggleTheme} checked={theme==="dark"} />
			</div>
			</div>
			</div>
			</ThemeContext.Provider>
		</>
	);
}

export default App;
