import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Footer from "../common/Footer";

// import aboutusImg from "../../images_files/about-us.jpg"

const AdminPolicyComp = ({ isAuthenticated }) => {

    const [AdminPolicy, setAdminPolicy] = useState("");

    useEffect(() => {
      const fetchAdminPolicy = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-AdminPolicy");
        const json = await resp.json();
        if (resp.ok) {
          setAdminPolicy(json);
        }
      };
  
      fetchAdminPolicy();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
            <div className="col-md-12 d-flex justify-content-end">
            <Breadcrumb>
              <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
              <Breadcrumb.Item active>Admin Policy</Breadcrumb.Item>
            </Breadcrumb>
            </div>
            <div className="col-md-12 bg-primary my-3 p-5" align="center" >
            
             {/* <img src={aboutusImg} alt={"About Us"} height={"300px"} width={"100%"}/> */}
             <h2 className="text-dark">Admin Policy</h2>
            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {AdminPolicy &&
                      AdminPolicy.map((adminpolicy) => (
                        <p
                         href=""
                          key={adminpolicy._id}
                        >
                          {adminpolicy.adminPolicy}
                        </p>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
          <Footer/>
		</section>
	);
};

AdminPolicyComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(AdminPolicyComp);
