import React, { useState } from "react";
import { connect } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import { registerusers } from "../../actions/auth2";
import PropTypes from "prop-types";
import Alert2 from "../layout/Alert2";
import { setAlert2 } from "../../actions/alert2";
import Footer from "../common/Footer";

const UsersRegister = ({ setAlert2, registerusers, isAuthenticated2 }) => {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    pass: "",
    pass2: "",
    phn: "",
  });

  const { fname, lname, email, pass, pass2, phn } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    console.log("Form data", e);
    e.preventDefault();
    if (pass !== pass2) {
      setAlert2("Password do not match", "danger");
    } else {
      registerusers({ fname, lname, email, pass, pass2, phn });
    }
  };

  // Redirect if logged in
  if (isAuthenticated2) {
    return <Redirect to="/admin/dashboard" />;
  }

  return (
    <div className="register-form">
      <h1 className="heading">Sign Up</h1>
      <p className="lead">
        <i className="fas fa-user"></i> Create Your Account
      </p>
      <Alert2 />
      <br />
      <form className="form" onSubmit={(e) => onSubmit(e)}>
        <div className="form-group">
          <input
            type="text"
            placeholder="First Name"
            name="fname"
            value={fname}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            placeholder="Last Name"
            name="lname"
            value={lname}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className="form-group">
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            value={email}
            onChange={(e) => onChange(e)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            name="pass"
            minLength="6"
            value={pass}
            onChange={(e) => onChange(e)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Confirm Password"
            name="pass2"
            minLength="6"
            value={pass2}
            onChange={(e) => onChange(e)}
          />
        </div>

        <div className="form-group">
          <input
            type="number"
            placeholder="Confirm Password"
            name="phn"
            maxLength="10"
            value={phn}
            onChange={(e) => onChange(e)}
          />
        </div>

        <input type="submit" className="btn btn-primary" value="Register" />
      </form>
      <p className="link">
        Already have an account? <Link to="/login">Sign In</Link>
      </p>
      <Footer />
    </div>
  );
};

UsersRegister.propTypes = {
  setAlert2: PropTypes.func.isRequired,
  register: PropTypes.func.isRequired,
  isAuthenticated2: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated2: state.auth.isAuthenticated2,
});

export default connect(mapStateToProps, { setAlert2, registerusers })(
  UsersRegister
);
