import React, { Fragment,useState } from "react";
import ReactSwitch from "react-switch";

import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";

import Navbar from "react-bootstrap/Navbar";

const NavbaR = ({ auth: { user, isAuthenticated, loading }, logout }) => {
  function calcTime(city, offset) {
    var b = new Date();
    var utc = b.getTime() + b.getTimezoneOffset() * 60000;
    var nd = new Date(utc + 3600000 * offset);
    return nd.getHours();
  }

  var showime = calcTime("Los Angeles", "-8");

  function calcTime(city, offset) {
    var b = new Date();
    var utc = b.getTime() + b.getTimezoneOffset() * 60000;
    var nd = new Date(utc + 3600000 * offset);
    return nd.getHours();
  }

  var showime = calcTime("Los Angeles", "-8");

  const [theme, setTheme] = useState("light");
  const toggleTheme = () => {
    setTheme((curr) => (curr === "light" ? "dark" : "light"));
  };





  const authLinks = (
    <nav className="main-header w-100 navbar navbar-expand navbar-white navbar-light">
      <ul className="navbar-nav">
        <li className="nav-item">
          <a
            className="nav-link"
            data-widget="pushmenu"
            href="#/"
            role="button"
          >
            <i className="fas fa-bars" />
          </a>
        </li>
        <li className="nav-item d-none d-sm-inline-block">
          <a href="/" className="nav-link text-warning">
            Home
          </a>
        </li>
        <li className="nav-item d-none d-sm-inline-block"></li>
      </ul>
      <ul className="navbar-nav ml-auto">
        <li className="nav-item">
          <a
            className="nav-link"
            data-widget="navbar-search"
            href="#/"
            role="button"
          >
            <i className="fas fa-search" />
          </a>
          <div className="navbar-search-block">
            <form className="form-inline">
              <div className="input-group input-group-sm">
                <input
                  className="form-control form-control-navbar"
                  type="search"
                  placeholder="Search"
                  aria-label="Search"
                />
                <div className="input-group-append">
                  <button className="btn btn-navbar" type="submit">
                    <i className="fas fa-search" />
                  </button>
                  <button
                    className="btn btn-navbar"
                    type="button"
                    data-widget="navbar-search"
                  >
                    <i className="fas fa-times" />
                  </button>
                </div>
              </div>
            </form>
          </div>
        </li>

        <li className="nav-item dropdown">
          <a className="nav-link" data-toggle="dropdown" href="#/">
            <i className="fa fa-user" />
          </a>
          <div
            className="dropdown-menu dropdown-menu-lg dropdown-menu-right"
            align="center"
          >
            <div style={{ marginTop: "", textAlign: "center" }}>
              <h5 className="font-weight-bold">
                Welcome, {user && user.name} ðŸ‘‹
              </h5>
              {(() => {
                if (showime >= 3 && showime < 12) {
                  return (
                    <div>
                      {" "}
                      Good Morning <i className="fa fa-sun text-warning"></i>
                    </div>
                  );
                } else if (showime >= 12 && showime < 18) {
                  return (
                    <div>
                      {" "}
                      Good Afternoon <i className="fa fa-sun text-warning "></i>{" "}
                    </div>
                  );
                } else if (showime >= 18 && showime < 23) {
                  return (
                    <div>
                      {" "}
                      Good Evening <i className="fa fa-moon text-dark"></i>
                    </div>
                  );
                } else {
                  return (
                    <div>
                      {" "}
                      Good Evening <i className="fa fa-moon text-dark"></i>
                    </div>
                  );
                }
              })()}
            </div>
            <br />
            <Link
              className="d-flex justify-content-center"
              to="/admin/dashboard"
            >
              <i className="fas fa-user"></i>{" "}
              <span className="hide-sm">Dashboard</span>
            </Link>

            <br />

            <Link
              className="d-flex justify-content-center"
              onClick={logout}
              to="/"
              replace
            >
              <i className="fa fa-arrow-right"></i>{" "}
              <span className="hide-sm">Logout</span>
            </Link>
          </div>

          <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right"></div>
        </li>
      </ul>
    </nav>
  );
  const guestLinks = (
    <>
      <div className="container-fluid p-3">
        <div className="row w-100">
          <div className="col-md-3">
            <a href="/">Logo</a>
          </div>
          <div className="col-md-9 d-flex justify-content-end">
            <ul className="list-unstyled">
              <li className="list-inline-item text-light nav-links">
                <a href="/" className="text-decoration-none">
                  Home
                </a>
              </li>
              <li className="list-inline-item text-light nav-links">
                <a href="/" className="text-decoration-none">
                  Pages
                </a>
              </li>
              <li className="list-inline-item text-light nav-links">
                <a href="/" className="text-decoration-none">
                  Products
                </a>
              </li>
              <li className="list-inline-item text-light nav-links">
                <a href="/" className="text-decoration-none">
                  Categories
                </a>
              </li>
              <li className="list-inline-item text-light nav-links">
                <a href="/" className="text-decoration-none">
                  Connect Now
                </a>
              </li>
              <div className="toogle-theme">
            {/* <h4 className="text-info font-weight-bold d-flex justify-content-center"></h4> */}
            <div className="m-2 d-flex justify-content-center">
              <label className="text-theme font-weight-bold">
                {theme === "light" ? (
                  <i class="fa fa-sun-o"></i>
                ) : (
                  <i class="fa fa-moon-o"></i>
                )}
              </label>{" "}
              &nbsp;&nbsp;
              <ReactSwitch onChange={toggleTheme} checked={theme === "dark"} />
            </div>
          </div>
            </ul>
          </div>
        </div>
      </div>
    </>
  );

  return (
    <nav className="navbar bg-dark" style={{}}>
      {!loading && (
        <Fragment>{isAuthenticated ? authLinks : guestLinks}</Fragment>
      )}
    </nav>
  );
};

Navbar.propTypes = {
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logout })(NavbaR);
