import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
// import icon from "../img/user.png";
import {} from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
import DataTable from "react-data-table-component";

const ManageSubCategories = ({ auth: { user } }) => {
  const [sub_categoryData, setsub_categoryData] = useState([]);
  const [Search, setSearch] = useState("");
  const [FilterValue, setFilterValue] = useState("");


  const getsub_categoryData = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/Fetch-SubCategories");
      setsub_categoryData(resp.data);
      setFilterValue(resp.data);
    } catch (error) {
      console.log(error);
    }
  };


  const Collumns = [
    {
      name: "Name",
      selector: (row) => row.sub_category_name,
      sortable: true,
    },
    {
      name: "Image",
      selector: (row) => (
        <img
          width={70}
          height={70}
          src={row.sub_category_image}
          alt={"Florida Fashion"}
        />
      ),
      sortable: true,
    },
    {
      name: "Parent Category",
      selector: (row) =>row.parent_category,
      sortable: true,
    },
    {
      name: "Title",
      selector: (row) => row.sub_category_title,
      sortable: true,
    },
    {
      name: "Description",
      selector: (row) => row.sub_category_desc,
      sortable: true,
    },
    {
      name: "Edit",
      cell: (row) => (
        <a href={`/edit?id=${row._id}`} className="btn btn-success">
          Edit
        </a>
      ),
    },
    {
      name: "Delete",
      cell: (row) => <button className="btn btn-danger">Delete</button>,
    },
  ];

  useEffect(() => {
    getsub_categoryData();
  }, []);
  
  useEffect(()=>{
    const result=sub_categoryData.filter(sub_category=>{
      return sub_category.sub_category_name.toLowerCase().match(Search.toLowerCase());
    });
    setFilterValue(result)
  },[Search])
    
    
  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Manage Sub Category</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-12">
                <DataTable
                  title="Category Data"
                  columns={Collumns}
                  data={FilterValue}
                  pagination
                  fixedHeader
                  selectableRows
                  subHeader
                  subHeaderComponent={
                    <input
                      type="text"
                      placeholder="Search Here...."
                      className="form-control w-25"
                      value={Search}
                      onChange={(e)=>setSearch(e.target.value)}
                    />
                  }
                />
              </div>
            </div>
          </div>
        </section>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
ManageSubCategories.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(ManageSubCategories);
