import React, { useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import axios from "axios";
import AsideBar from "./common/AsideBar";
// import { Redirect } from "react-router-dom";
import Footer from "./common/Footer";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const AddartCollectionss = ({ auth: { user } }) => {
  const [artCollectionss_name, setartCollectionss_name] = useState("");
  const [artCollectionss_short_desc, setartCollectionss_short_desc] =useState("");
  const [taq, settaq] = useState("");
  const [maq, setmaq] = useState("");
  const [price, setprice] = useState("");
  const [artCollectionss_category, setartCollectionss_category] = useState("");
  const [artCollectionss_sub_category, setartCollectionss_sub_category] =useState("");
  const [brands_name, setbrands_name] = useState("");
  const [sp_price, setsp_price] = useState("");
  const [file, setFile] = useState("");

  // const history = useHistory();

  const setsetartCollectionss_name = (e) => {
    const { value } = e.target;
    setartCollectionss_name(value);
  };

  const setShortDesc = (e) => {
    const { value } = e.target;
    setartCollectionss_short_desc(value);
  };

  const settotalAllowedQuantity = (e) => {
    const { value } = e.target;
    settaq(value);
  };

  const setMinimumOrderQuantity = (e) => {
    const { value } = e.target;
    setmaq(value);
  };
  const setPrice = (e) => {
    const { value } = e.target;
    setprice(value);
  };
  const setSpPrice = (e) => {
    const { value } = e.target;
    setsp_price(value);
  };
  const setartCollectionsscategory = (e) => {
    const { value } = e.target;
    setartCollectionss_category(value);
  };

  const setartCollectionssSsubCcategory = (e) => {
    const { value } = e.target;
    setartCollectionss_sub_category(value);
  };

  const SetBrandsNAME = (e) => {
    const { value } = e.target;
    setbrands_name(value);
  };
  const setimgfile = (e) => {
    setFile(e.target.files[0]);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
