import React, { useState,useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";
// import icon from "../img/user.png";
// import { logout } from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import ReactQuill from "react-quill";
import EditorToolbar, { modules, formats } from "./common/EditorToolbar";
import "react-quill/dist/quill.snow.css";
import Footer from './common/Footer';



const FAQs =({ auth: { user } })=> {
 
  let history = useHistory();
  const [userInfo, setuserInfo] = useState({
    question: "",
    answer: "",
  });

  const onquestion = (value) => {
    setuserInfo({ ...userInfo, question: value });
  };
  const onanswer = (value) => {
    setuserInfo({ ...userInfo, answer: value });
  };


  useEffect(() => {
    const fetchFaq = async () => {
      const resp = await fetch("http://localhost:5000/Fetch-Faq");
      const json = await resp.json();
      if (resp.ok) {
        setuserInfo(json);
      }
    };

    fetchFaq();
  }, []);


  const [isError] = useState(null);
  const addDetails = async (event) => {
    try {
      event.preventDefault();
      event.persist();

      axios
        .post(`http://localhost:5000/FAQS`, {
          question: userInfo.question,
          answer: userInfo.answer,
        })
        .then((res) => {
          if (res.data.success === true) {
            history.push("/");
          }
        });
    } catch (error) {
      throw error;
    }
  };

   
    return (
      <div className="wrapper">
        <div>
          <nav className="main-header navbar navbar-expand navbar-white navbar-light">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a
                  className="nav-link"
                  data-widget="pushmenu"
                  href="#/"
                  role="button"
                >
                  <i className="fas fa-bars" />
                </a>
              </li>
              <li className="nav-item d-none d-sm-inline-block">
                <a href="index3.html" className="nav-link">
                  Home
                </a>
              </li>
              <li className="nav-item d-none d-sm-inline-block"></li>
            </ul>
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <a
                  className="nav-link"
                  data-widget="navbar-search"
                  href="#/"
                  role="button"
                >
                  <i className="fas fa-search" />
                </a>
                <div className="navbar-search-block">
                  <form className="form-inline">
                    <div className="input-group input-group-sm">
                      <input
                        className="form-control form-control-navbar"
                        type="search"
                        placeholder="Search"
                        aria-label="Search"
                      />
                      <div className="input-group-append">
                        <button className="btn btn-navbar" type="submit">
                          <i className="fas fa-search" />
                        </button>
                        <button
                          className="btn btn-navbar"
                          type="button"
                          data-widget="navbar-search"
                        >
                          <i className="fas fa-times" />
                        </button>
                      </div>
                    </div>
                  </form>
                </div>
              </li>

              <li className="nav-item dropdown">
                <a className="nav-link" data-toggle="dropdown" href="#/">
                  <i className="fa fa-user" />
                </a>
                <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                  <div style={{ marginTop: "", textAlign: "center" }}>
                    <h5>Welcome, {user && user.name}</h5>
                  </div>
                </div>
              </li>
            </ul>
          </nav>
        </div>
        <div>
          <AsideBar />
        </div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">FAQs</h1>
                </div>
              </div>
            </div>
          </div>
          <section className="content">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">
                  <div className="editor">
                    <div className="col-md-12">
                    <form onSubmit={addDetails} className="update__forms">
                  <h3 className="myaccount-content"> Add </h3>
                  <div className="form-row">
                  
                    <div className="clearfix"></div>
                    <div className="form-group col-md-12 editor">
                      <label className="font-weight-bold">
                        {" "}
                        Question <span className="required"> * </span>{" "}
                      </label>
                      <EditorToolbar toolbarId={"t1"} />
                      <ReactQuill
                        theme="snow"
                        value={userInfo.question}
                        onChange={onquestion}
                        placeholder={"Write something awesome..."}
                        modules={modules("t1")}
                        formats={formats}
                      />
                    </div>
                    <br />
                    <div className="form-group col-md-12 editor">
                      <label className="font-weight-bold">
                        {" "}
                        Answer{" "}
                      </label>
                      <EditorToolbar toolbarId={"t2"} />
                      <ReactQuill
                        theme="snow"
                        value={userInfo.answer}
                        onChange={onanswer}
                        placeholder={"Write something awesome..."}
                        modules={modules("t2")}
                        formats={formats}
                      />
                    </div>
                    <br />
                    {isError !== null && (
                      <div className="errors"> {isError} </div>
                    )}
                    <div className="form-group col-sm-12" align="center">
                      <button type="submit" className="btn btn-outline-primary btn__theme">
                        {" "}
                        Save{" "}
                      </button>
                    </div>
                  </div>
                </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        </div>
        <div>
          <Footer/>
        </div>
      </div>
    );
  }

FAQs.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(FAQs);
