import React, { useState,useEffect} from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import AsideBar from "./common/AsideBar";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useParams } from "react-router-dom";

const UserManagement = ({ auth: { user } }) => {
  //Inserting Form Data
  


  const [newUser, setNewUser] = useState({
    category_name: "",
    category_image: "",
    category_title: "",
    category_desc: "",
  });

  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };


  const { id } = useParams();

  const getUserData = async () => {
    try {
      const resp = await axios.get(`http://localhost:5000/getuser/${id}`);
      setNewUser(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUserData();
  },[]);


  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("category_name", newUser.category_name);
    formData.append("category_image", newUser.category_image);
    formData.append("category_title", newUser.category_title);
    formData.append("category_desc",   newUser.category_desc);

 const updateUsers=axios({
      method: "PATCH",
      url: `http://localhost:5000/updateusers/${id}`,
      data: JSON.stringify(newUser),
      headers: { "Content-Type": "application/json" },
    });
      if(updateUsers){
        console.log(updateUsers);
      }
      else{
        console.log("No update users");
      }
  };



  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="/" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5 className="d-flex justify-content-center">
                    Welcome, {user && user.name}
                  </h5>
                </div>
                {/* <Link className="d-flex justify-content-center" onClick={logout} to="/" replace>
									<i className="fas fa-sign-out-alt"></i>{" "}
									<span className="hide-sm"> &nbsp;Logout</span>
								</Link>
								<a href="/" >Logout</a> */}
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">Add Users</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#/">User Management</a>
                    </li>
                    <li className="breadcrumb-item active">Add Users</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <div></div>
          <section className="content">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">
                  <ul className="list-unstyled" align="center">
                    <li className="list-inline-item p-2 btn btn-primary">
                      {" "}
                      <i className="fa fa-user d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/users">
                        All Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-success">
                      {" "}
                      <i className="fa fa-user-plus d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/add-users">
                        Add Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-warning">
                      {" "}
                      <i className="fa fa-ban d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/suspend-users">
                        Suspended Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-danger">
                      {" "}
                      <i className="fas fa-user-times d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/delete-users">
                        Deleted Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-info">
                      {" "}
                      <i className="fa fa-envelope"></i>{" "}
                      <a className="text-dark" href="/admin/email-pending">
                        Pendin Emails
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </section>
          <div className="container">
            <div className="row">
              <div className="col-md-12 p-5">
                <Form
                  className="mt-3 shadow-lg bg-light p-5 rounded"
                  onSubmit={handleSubmit}
                >
                  <h4>Add User</h4>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Category Name</Form.Label>
                    <Form.Control
                      style={{
                        background: "aliceblue",
                        color: "black",
                        borderRadius: "22px",
                      }}
                      type="text"
                      name="category_name"
                      value={newUser.category_name}
                      onChange={handleChange}
                      placeholder="Category Name"
                      className="add-users-input"
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Category Image</Form.Label>
                    <Form.Control
                      style={{
                        background: "aliceblue",
                        color: "black",
                        borderRadius: "22px",
                      }}
                      type="text"
                      name="category_image"
                      value={newUser.category_image}
                      onChange={handleChange}
                      placeholder="Category Image"
                      className="add-users-input"
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Category Title</Form.Label>
                    <Form.Control
                      style={{
                        background: "aliceblue",
                        color: "black",
                        borderRadius: "22px",
                      }}
                      type="text"
                      name="category_title"
                      value={newUser.category_title}
                      onChange={handleChange}
                      placeholder="Category Title"
                      className="add-users-input"
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Category Descriptons</Form.Label>
                    <Form.Control
                      style={{
                        background: "aliceblue",
                        color: "black",
                        borderRadius: "22px",
                      }}
                      type="number"
                      name="category_desc"
                      value={newUser.category_desc}
                      onChange={handleChange}
                      placeholder="Category Descriptons"
                      className="add-users-input"
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      style={{
                        background: "aliceblue",
                        color: "black",
                        borderRadius: "22px",
                      }}
                      type="text"
                      name="pass"
                      value={newUser.pass}
                      onChange={handleChange}
                      placeholder="Password"
                      className="add-users-input"
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Role</Form.Label>
                    <Form.Select
                      style={{
                        background: "aliceblue",
                        color: "black",
                        borderRadius: "22px",
                      }}
                      name="role"
                      value={newUser.role}
                      onChange={handleChange}
                      className="add-users-input"
                    >
                      <option value={"-----"}>-----</option>
                      <option value={"Admin"}>Admin</option>
                      <option value={"User"}>User</option>
                    </Form.Select>
                  </Form.Group>

                  <Form.Group
                    className="mb-3"
                    controlId="formBasicEmail"
                    align="center"
                  >
                    <Form.Group
                      className="mb-3"
                      controlId="formBasicEmail"
                      align="center"
                    >
                      <Button
                        type="submit"
                        className="btn btn-outline-info w-25 form-control shadow-lg text-light"
                      >
                        Add
                      </Button>
                    </Form.Group>
                  </Form.Group>
                </Form>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div>
        <footer />
      </div>
    </div>
  );
};
UserManagement.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(UserManagement);
