import React, { useState,useEffect} from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import AsideBar from "./common/AsideBar";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useParams } from "react-router-dom";
import Footer from "./common/Footer";
const UserManagement = ({ auth: { user } }) => {
  //Inserting Form Data
  


  const [collection_title, setartcollection_title] = useState("");
  const [artCollectionss_name, setartCollectionss_name] = useState("");
  const [artCollectionss_short_desc, setartCollectionss_short_desc] =useState("");
  const [artCollectionss_category, setartCollectionss_category] = useState("");
  const [artCollectionss_price, setartCollectionss_price] = useState("");
  const [collectionsImg, setcollectionsImg] = useState("");



  const setCollectionsTitle = (e) => {
    const { value } = e.target;
    setartcollection_title(value);
  };

  const setCollectionsName = (e) => {
    const { value } = e.target;
    setartCollectionss_name(value);
  };

  const setShortDesc = (e) => {
    const { value } = e.target;
    setartCollectionss_short_desc(value);
  };

  const setCollectionCategory = (e) => {
    const { value } = e.target;
    setartCollectionss_category(value);
  };

  const stCollectionPrice = (e) => {
    const { value } = e.target;
    setartCollectionss_price(value);
  };

  const setimgfile = (e) => {
    setcollectionsImg(e.target.files[0]);
  };


  const { id } = useParams();



  const [newUser, setNewUser] = useState("")
  const getUserData = async () => {
    try {
      const resp = await axios.get(`http://localhost:5000/getarts/${id}`);
      setNewUser(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getUserData();
  },[]);



  const [categoryData, setcategoryData] = useState([]);

  const getcategoryData = async () => {
    try {
      const resp = await axios.get("http://localhost:5000/Fetch-Categories");
      setcategoryData(resp.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getcategoryData();
  }, []);




  const handleSubmit = (e) => {
    e.preventDefault();
    var formData = new FormData();
    formData.append("collection_title", collection_title);
    formData.append("artCollectionss_name", artCollectionss_name);
    formData.append("artCollectionss_short_desc", artCollectionss_short_desc);
    formData.append("artCollectionss_category", artCollectionss_category);
    formData.append("artCollectionss_price", artCollectionss_price);
    formData.append("collectionsImg", collectionsImg);

 const updateUsers=axios({
      method: "PATCH",
      url: `http://localhost:5000/updateusers/${id}`,
      data: JSON.stringify(newUser),
      headers: { "Content-Type": "application/json" },
    });
      if(updateUsers){
        console.log(updateUsers);
      }
      else{
        console.log("No update users");
      }
  };



  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="/" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5 className="d-flex justify-content-center">
                    Welcome, {user && user.name}
                  </h5>
                </div>
              
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div>
        <div className="content-wrapper">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0">Add Users</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#/">User Management</a>
                    </li>
                    <li className="breadcrumb-item active">Add Users</li>
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <div></div>
          <section className="content">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">
                  <ul className="list-unstyled" align="center">
                    <li className="list-inline-item p-2 btn btn-primary">
                      {" "}
                      <i className="fa fa-user d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/users">
                        All Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-success">
                      {" "}
                      <i className="fa fa-user-plus d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/add-users">
                        Add Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-warning">
                      {" "}
                      <i className="fa fa-ban d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/suspend-users">
                        Suspended Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-danger">
                      {" "}
                      <i className="fas fa-user-times d-inline"></i>{" "}
                      <a className="text-dark" href="/admin/delete-users">
                        Deleted Users
                      </a>
                    </li>
                    <li className="list-inline-item p-2 btn btn-info">
                      {" "}
                      <i className="fa fa-envelope"></i>{" "}
                      <a className="text-dark" href="/admin/email-pending">
                        Pendin Emails
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </section>
          <div className="container">
            <div className="row">
              <div className="col-md-12 p-5">
                  <h4>Product Edit</h4>
                  <Form className="mt-3 shadow-lg bg-light p-5 rounded"
                  onSubmit={handleSubmit}>
                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Title</Form.Label>
                  <Form.Control
                    type="text"
                    name="collection_title"
                    value={newUser.collection_title}
                    onChange={setCollectionsTitle}
                    placeholder="Art Collections Title"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="artCollectionss_name"
                    value={newUser.artCollectionss_name}
                    onChange={setCollectionsName}
                    placeholder="Art Collections Name"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Short Desc</Form.Label>
                  <Form.Control
                    type="text"
                    name="artCollectionss_short_desc"
                    value={newUser.artCollectionss_short_desc}
                    onChange={setShortDesc}
                    placeholder="Art Collections Short Desc"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections's Category</Form.Label>
                  <Form.Select
                    name="artCollectionss_category"
                    value={newUser.artCollectionss_category}
                    className="form-control"
                    onChange={setCollectionCategory}
                  >
                    <option>---------</option>
                    {categoryData.map((e) => {
                      return <option key={e.category_name}>{e.category_name}</option>;
                    })}
                  </Form.Select>
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Price</Form.Label>
                  <Form.Control
                    type="number"
                    name="artCollectionss_price"
                    value={newUser.artCollectionss_price}
                    onChange={stCollectionPrice}
                    placeholder="Art Collections Price"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Art Collections Thumbnail</Form.Label>
                  <Form.Control
                    type="file"
                    name="collectionsImg"
                    onChange={setimgfile}
                    placeholder="Art Collections Thumbnail"
                  />
                </Form.Group>

                <Form.Group
                  className=""
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Button className="btn btn-outline-info text-light" variant="primary" type="submit">
                    Update
                  </Button>
                </Form.Group>
              </Form>

                 
              </div>
            </div>
          </div>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
UserManagement.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(UserManagement);
