import React, { Component } from "react";
import Footer from "./common/Footer";
import { Link } from "react-router-dom";

export default class UserLogin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      pass: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    const { email, pass } = this.state;
    console.log(email, pass);
    fetch("http://localhost:5000/login", {
      method: "POST",
      crssDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({ email, pass }),
    })
      .then((res) => res.json("ok"))
      .then((data) => {
        console.log(data, "User Logged In Successfully");
        if (data.status === "ok") {
          alert("User Logged In Successfully");
          window.localStorage.setItem("token", data.data);
          window.location.href = "/user/dashboard";
        }
      });
  }

  render() {
    return (
      <div>
        <div className="register-form text-light" align="center">
          <h1 className="heading">Sign In</h1>
          <p className="lead">
            <i className="fas fa-user"></i> Sign In To Your Account
          </p>
        </div>
        <br />
        <div className="container">
          <div className="row">
            <div className="col-md-12 d-flex justify-content-center text-light">
              <form className="form" onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <input
                    type="email"
                    placeholder="Email Address"
                    name="email"
                    className="form-control"
                    // value={email}
                    onChange={(e) => this.setState({ email: e.target.value })}
                  />
                </div>

                <div className="form-group">
                  <input
                    type="password"
                    placeholder="Password"
                    name="pass"
                    className="form-control"
                    minLength="6"
                    // value={pass}
                    onChange={(e) => this.setState({ pass: e.target.value })}
                  />
                </div>

                <div className="form-group" align="center">
                  <input
                    type="submit"
                    className="btn btn-primary"
                    value="Sign In"
                  />
                </div>
              </form>
            </div>
          </div>
        </div>
        <p className="link text-light" align="center">
          Don't have an account? <Link to="/user/register">Sign Up</Link>
        </p>
        <Footer />
      </div>
    );
  }
}
