import React, { useState } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import icon from "../img/user.png";
import { logout } from "../actions/auth";
import axios from "axios";
import AsideBar from "./common/AsideBar";
import { } from "react-router-dom"
import Footer from "./common/Footer";

const AddProducts = ({ auth: { user } }, isAuthenticated) => {
  const [products_name, setproducts_name] = useState("");
  const [products_short_desc, setproducts_short_desc] = useState("");
  const [taq, settaq] = useState("");
  const [maq, setmaq] = useState("");
  const [price, setprice] = useState("");
  const [sp_price, setsp_price] = useState("");
  const [products_type, setproducts_type] = useState("");
  const [products_sku, setproducts_sku] = useState("");
  const [products_sku_status, setproducts_sku_status] = useState("");
  const [products_big_desc, setproducts_big_desc] = useState("");

  const [file, setFile] = useState("");

  // const history = useNavigate();

  const setdata = (e) => {
    const { value } = e.target;
    setproducts_name(value);
    setproducts_short_desc(value);
    settaq(value);
    setmaq(value);
    setprice(value);
    setsp_price(value);
    setproducts_type(value);
    setproducts_sku(value);
    setproducts_sku_status(value);
    setproducts_big_desc(value);
  };

