import React, { useState, useEffect } from "react";
import Footer from "./common/Footer";
import AsideBar from "./common/Asidebar2";
import { useParams } from "react-router-dom";
import axios from "axios";
import DataTable from "react-data-table-component";

const AdminOwnCollections = () => {

    const [categoryData, setcategoryData] = useState([]);

    const getcategoryData = async () => {
      try {
        const resp = await axios.get("http://localhost:5000/Fetch-Categories");
        setcategoryData(resp.data);
      } catch (error) {
        console.log(error);
      }
    };
  
    const Collumns = [
      {
        name: "Category Name",
        selector: (row) => row.category_name,
      },
      {
        name: "Category Image",
        selector: (row) => (
          <img
            width={70}
            height={70}
            src={require(`../uploads/${row.category_image}`).default}
            alt={"NFT"}
          />
        ),
      },
      {
        name: "Category Title",
        selector: (row) => row.category_title,
      },
      {
        name: "Category Description",
        selector: (row) => row.category_desc,
      },
      {
        name: "Edit",
        cell: (row) => (
          <a href={`/admin/category-edit/${row._id}`} className="btn btn-success">
            <i className="fa fa-pen"></i>
          </a>
        ),
      },
      {
        name: "Delete",
        cell: (row) => (
          <button
            className="btn btn-danger"
            onClick={() => deletecategory(row._id)}
          >
            <i className="fa fa-times"></i>
          </button>
        ),
      },
    ];
  
    useEffect(() => {
      getcategoryData();
    }, []);
  
    const deletecategory = async (id) => {
      await axios
        .delete(`http://localhost:5000/deleteCategory/${id}`, {
          headers: {
            "Content-Type": "application/json",
          },
        })
        .then((data) => {
          console.log(data, "Category Updated");
          if (data.status === 200) {
            alert(`Category Has Been Deleted Successfully`);
            window.location.href = "/admin/category-list";
          }
        });
    };
  




    return (
        <div>
            <div className="wrapper">
                <div>
                    <nav className="main-header navbar navbar-expand navbar-white navbar-light">
                        <ul className="navbar-nav">
                            <li className="nav-item">
                                <a
                                    className="nav-link"
                                    data-widget="pushmenu"
                                    href="#/"
                                    role="button"
                                >
                                    <i className="fas fa-bars" />
                                </a>
                            </li>
                            <li className="nav-item d-none d-sm-inline-block">
                                <a href="/" className="nav-link text-warning">
                                    Home
                                </a>
                            </li>
                            <li className="nav-item d-none d-sm-inline-block"></li>
                        </ul>
                        <ul className="navbar-nav ml-auto">
                            <li className="nav-item">
                                <a
                                    className="nav-link"
                                    data-widget="navbar-search"
                                    href="#/"
                                    role="button"
                                >
                                    <i className="fas fa-search" />
                                </a>
                                <div className="navbar-search-block">
                                    <form className="form-inline">
                                        <div className="input-group input-group-sm">
                                            <input
                                                className="form-control form-control-navbar"
                                                type="search"
                                                placeholder="Search"
                                                aria-label="Search"
                                            />
                                            <div className="input-group-append">
                                                <button className="btn btn-navbar" type="submit">
                                                    <i className="fas fa-search" />
                                                </button>
                                                <button
                                                    className="btn btn-navbar"
                                                    type="button"
                                                    data-widget="navbar-search"
                                                >
                                                    <i className="fas fa-times" />
                                                </button>
                                            </div>
                                        </div>
                                    </form>
                                </div>
                            </li>

                            <li className="nav-item dropdown">
                                <a className="nav-link" data-toggle="dropdown" href="#/">
                                    <i className="fa fa-user" />
                                </a>
                                <div
                                    className="dropdown-menu dropdown-menu-lg dropdown-menu-right"
                                    align="center"
                                >
                                    <div style={{ marginTop: "", textAlign: "center" }}>
                                        <h5 className="text-dark text-center font-weight-bold">
                                            {" "}
                                            Welcome, <span></span> ðŸ‘‹ &nbsp;
                                            <button className="btn btn-info border-none" onClick={logout}
                                                style={{ border: "none", borderRadius: "0px" }}
                                            >
                                                Logout <i className="fa fa-sign-out"></i>
                                            </button>
                                        </h5>

                                    </div>

                                </div>
                                <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right"></div>
                            </li>
                        </ul>
                    </nav>
                </div>
                <div>
                    <AsideBar />
                </div>
                <div className="content-wrapper">

                    <div className="content-header">
                        <div className="container-fluid">
                            <div className="row mb-2">
                                <div className="col-sm-6"></div>
                            </div>
                        </div>
                    </div>
                    <section className="content">
                        <div className="container-fluid">
                            <div className="row d-flex justify-content-center">


                                <div className="col-md-12" align="center">
                                    <DataTable
                                        title="My Collections's Data"
                                        columns={Collumns}
                                        data={arts}
                                        pagination
                                        fixedHeader
                                        selectableRows
                                    />
                                </div>




                            </div>
                        </div>
                    </section>
                </div>
                <div>
                    <Footer />
                </div>
            </div>
        </div>
    );
}
export default AdminOwnCollections;
