import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";
import Logo from "../../images/virtual-art-gif2.gif";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/esm/Button";

const NavbaR = ({ auth: { user, isAuthenticated, loading }, logout }) => {
  function calcTime(city, offset) {
    var b = new Date();
    var utc = b.getTime() + b.getTimezoneOffset() * 60000;
    var nd = new Date(utc + 3600000 * offset);
    return nd.getHours();
  }

  var showime = calcTime("Los Angeles", "-8");

  function calcTime(city, offset) {
    var b = new Date();
    var utc = b.getTime() + b.getTimezoneOffset() * 60000;
    var nd = new Date(utc + 3600000 * offset);
    return nd.getHours();
  }

  var showime = calcTime("Los Angeles", "-8");

  const IsLoggedIn = window.localStorage.getItem("userLoggedIn");

  logout = () => {
    window.localStorage.clear();
    window.location.href = "/";
  };
  const authLinks = (
    <nav className="main-header navbar navbar-expand navbar-white navbar-light">
      <ul className="navbar-nav">
        <li className="nav-item">
          <a
            className="nav-link"
            data-widget="pushmenu"
            href="#/"
            role="button"
          >
            <i className="fas fa-bars" />
          </a>
        </li>
        <li className="nav-item d-none d-sm-inline-block">
          <a href="/" className="nav-link text-warning">
            Home
          </a>
        </li>
        <li className="nav-item d-none d-sm-inline-block"></li>
      </ul>
      <ul className="navbar-nav ml-auto">
        <li className="nav-item">
          <a
            className="nav-link"
            data-widget="navbar-search"
            href="#/"
            role="button"
          >
            <i className="fas fa-search" />
          </a>
          <div className="navbar-search-block">
            <form className="form-inline">
              <div className="input-group input-group-sm">
                <input
                  className="form-control form-control-navbar"
                  type="search"
                  placeholder="Search"
                  aria-label="Search"
                />
                <div className="input-group-append">
                  <button className="btn btn-navbar" type="submit">
                    <i className="fas fa-search" />
                  </button>
                  <button
                    className="btn btn-navbar"
                    type="button"
                    data-widget="navbar-search"
                  >
                    <i className="fas fa-times" />
                  </button>
                </div>
              </div>
            </form>
          </div>
        </li>

        <li className="nav-item dropdown">
          <a className="nav-link" data-toggle="dropdown" href="#/">
            <i className="fa fa-user" />
          </a>
          <div
            className="dropdown-menu dropdown-menu-lg dropdown-menu-right"
            align="center"
          >
            <div style={{ marginTop: "", textAlign: "center" }}>
              <h5 className="font-weight-bold">
                Welcome, {user && user.name} 👋
              </h5>
              {(() => {
                if (showime >= 3 && showime < 12) {
                  return (
                    <div>
                      {" "}
                      Good Morning <i className="fa fa-sun text-warning"></i>
                    </div>
                  );
                } else if (showime >= 12 && showime < 18) {
                  return (
                    <div>
                      {" "}
                      Good Afternoon <i className="fa fa-sun text-warning "></i>{" "}
                    </div>
                  );
                } else if (showime >= 18 && showime < 23) {
                  return (
                    <div>
                      {" "}
                      Good Evening <i className="fa fa-moon text-dark"></i>
                    </div>
                  );
                } else {
                  return (
                    <div>
                      {" "}
                      Good Evening <i className="fa fa-moon text-dark"></i>
                    </div>
                  );
                }
              })()}
            </div>

            <Link
              className="d-flex justify-content-center"
              to="/admin/dashboard"
            >
              <i className="fas fa-user"></i>{" "}
              <span className="hide-sm">Dashboard</span>
            </Link>
            <Link
              className="d-flex justify-content-center"
              onClick={logout}
              to="/"
              replace
            >
              <i className="fa fa-arrow-right"></i>{" "}
              <span className="hide-sm">Logout</span>
            </Link>
          </div>
          <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right"></div>
        </li>
      </ul>
    </nav>
  );
  const guestLinks = (
    <>
      <nav className="sc-tilXH bhipKb sticky-top bg-dark text-light">
        <div className="container">
          <div className="row">
            <div className="col-md-5">
              <ul
                className="list-unstyled d-flex"
                style={{ marginTop: "12px" }}
              >
                <li className="list-inline-item p-2">
                  <a className="text-light nav-lists" href="/auctions">
                    AUCTIONS
                  </a>
                </li>
                <li className="list-inline-item p-2">
                  <a className="text-light nav-lists" href="/arts">
                    VIEW ALL
                  </a>
                </li>
                <li className="list-inline-item p-2">
                  <a className="text-light nav-lists" href="/virtual-tour">
                    VIRTUAL GALLERY
                  </a>
                </li>
                <li className="list-inline-item p-2">
                  <a className="text-light nav-lists" href="/faqs">
                    FAQs
                  </a>
                </li>
              </ul>
            </div>
            <div className="col-md-4 p-2">
              <a className="text-light" href="/" style={{ marginLeft: "60px" }}>
                <img src={Logo} alt="" height={"100%"} width={"50%"} />
              </a>
            </div>
            <div className="col-md-3">
              {(() => {
                if (IsLoggedIn === "true") {
                  return (
                    <>
                      <ul className="list-unstyled" style={{ marginTop: "16px" }}>
                        <li className="list-inline-item"><a href="/user/dashboard" className="text-light nav-lists">DASHBOARD</a></li>
                        <li className="list-inline-item"><a href="" className="text-light nav-lists" onClick={logout}>LOGOUT</a></li>
                        <li className="list-inline-item"><a href="/create-bid" className="text-light nav-lists">ADD ARTS</a></li>
                      </ul>
                    </>
                  );
                } else {
                  return (
                    <>
                      {" "}
                      <ul className="list-unstyled" style={{ marginTop: "16px" }}>
                        <li className="list-inline-item">
                          <a
                            className="text-light bg-dark p-2 w-50 h-75 rounded"
                            href="/user/login"
                            align="center"
                          >
                            Sign In
                          </a>
                        </li>
                        <li className="list-inline-item">

                          <a
                            className="text-light bg-dark p-2 w-50 h-75 rounded"
                            href="/user/register"
                            align="center"
                          >
                            Register
                          </a>
                        </li>
                      </ul>

                    </>
                  );
                }
              })()}
            </div>
          </div>
        </div>
      </nav>
    </>
  );

  return (
    <>
      {!loading && (
        <Fragment>{isAuthenticated ? authLinks : guestLinks}</Fragment>
      )}
    </>
  );
};

Navbar.propTypes = {
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logout })(NavbaR);
