import React, { useState, useEffect } from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import "../../css/landing.css";
import Footer from "../common/Footer";
import axios from "axios";
// import Item from "./item";
// import Carousel2 from "react-elastic-carousel";
// import Col from "react-bootstrap/Col";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // Requires a Loader
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Logo from "../../images/virtual-art-gif2.gif";

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 2 },
  { width: 768, itemsToShow: 4 },
  { width: 1200, itemsToShow: 4 },
];

const Landing = ({ isAuthenticated }) => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [fname, setfname] = useState("");
  const [lname, setlname] = useState("");
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [country_name, setcountry_name] = useState("");

  // const history = useHistory();

  const setFname = (e) => {
    const { value } = e.target;
    setfname(value);
  };

  const setLname = (e) => {
    const { value } = e.target;
    setlname(value);
  };

  const setEmail = (e) => {
    const { value } = e.target;
    setemail(value);
  };

  const setPassword = (e) => {
    const { value } = e.target;
    setpassword(value);
  };

  const setCountryName = (e) => {
    const { value } = e.target;
    setcountry_name(value);
  };

  // adduser data

  const addUserData = async (e) => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("fname", fname);
    formData.append("lname", lname);
    formData.append("email", email);
    formData.append("password", password);
    formData.append("country_name", country_name);

    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    await axios
      .post("http://localhost:5000/register", formData, config)
      .then((data) => {
        console.log(data, "Category Added");
        if (data.status === 200) {
          alert(`Signed Up Successfully`);
          window.location.href = "/auctions";
        }
      });
  };


  let [OnGoingAuctions, setOnGoingAuctions] = useState("");

  useEffect(() => {
    const fetchOnGoingAuctions = async () => {
      try {
        const resp = await axios.get(`http://localhost:5000/OngoingAuctionsArts`);
        for (let i = 0; i < resp.data.length; i++) {
          setOnGoingAuctions(resp.data[i]);
        }

      } catch (error) {
        console.log(error);
      }
    };


    fetchOnGoingAuctions();
  }, []);


  let [OnGoingAuctions2, setOnGoingAuctions2] = useState("");

  useEffect(() => {
    const fetchOnGoingAuctions2 = async () => {
      try {
        const resp = await axios.get(`http://localhost:5000/OngoingAuctionsArts`);

        setOnGoingAuctions2(resp.data);


      } catch (error) {
        console.log(error);
      }
    };


    fetchOnGoingAuctions2();
  }, []);




   console.log(OnGoingAuctions.status);
  const IsLoggedIn = window.localStorage.getItem("userLoggedIn");

  if (IsLoggedIn !== "true") {
    return <Redirect to="/user/login" />;
  }


  if (isAuthenticated) {
    return <Redirect to="/admin/dashboard" />;
  }

  return (
    <>
      <div className="navNsec m-0">
        {(() => {
          if (OnGoingAuctions.status === "Ongoing") {

            {
              OnGoingAuctions2 &&
                OnGoingAuctions2.map((e) => (
                  <>
                    <div className="auctions">
                      <div className="container" align="center">
                        <div className="row d-flex justify-content-center">
                          <div className="collections-parent col-md-3 m-3 p-2 collections">
                            <a href="/">
                              <img className="w-100" src={require(`../../uploads/${e.collectionsImg}`).default} alt="" />
                            </a>
                            <div className="info" align="center">
                              <p>{e.collection_title}</p>
                              <p>{e.artCollectionss_name}</p>
                              <br />
                              <p>PRICE</p>
                              <p>{e.artCollectionss_price}</p>
                            </div>
                          </div>

                        </div>
                      </div>
                    </div>
                  </>
                ))
            }

          }




          else {
            return false;
          }
        })()}
      </div>
     
      <br />
      <br />

      <Footer />
    </>
  );
};

Landing.propTypes = {
  isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(Landing);
