import React, { useState,useRef } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import icon from "../img/user.png";
import { logout } from "../actions/auth";
// import axios  from "axios";
import AsideBar from "./common/AsideBar";
import { Redirect } from "react-router-dom";
import Footer from "./common/Footer";
import JoditEditor from 'jodit-react';

const AboutUs = ({ auth: { user } },isAuthenticated) => {
  const [productsData, setUser] = useState({
    products_name: "",
    products_short_desc: "",
    taq: "",
    maq: "",
    products_img: "",
    price: "",
    sp_price: "",
    products_type: "",
    products_sku: "",
    products_sku_status: "",
    products_big_desc: "",
  });
   const{products_name,
   products_short_desc,
   taq,
   maq,
   products_img,
   price,
   sp_price,
   products_type,
   products_sku,
   products_sku_status,
   products_big_desc,}=productsData;
  let name, value;

  const handleInputs = (e) => {
    console.log(e);
    name = e.target.name;
    value = e.target.value;
    setUser({ ...productsData, [name]: value });
  };


  const AboutUsData=async()=>{
    const postURL = "http://localhost:5000/Add-Products" //Our previously set up route in the backend
    fetch(postURL, {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ // We should keep the fields consistent for managing this data later
          products_name,
          products_short_desc,
          taq,
          maq,
          products_img,
          price,
          sp_price,
          products_type,
          products_sku,
          products_sku_status,
          products_big_desc,
          clockedIn:false,
          dates:[]
        })
    })
    .then(()=>{
        // Once posted, the user will be notified 
        alert('Successfully Added The Product');
    });
  }
	// Redirect if logged in
	// if (isAuthenticated) {
	// 	return <Redirect to="/add-products" />;
	// }

  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
       <AsideBar/>
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">About Us</h1>
              </div>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              
            </div>
          </div>
        </section>
      </div>
      <div>
       <Footer/>
      </div>
    </div>
  );
};
AboutUs.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(AboutUs);
