import React , {useEffect, useState} from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import {} from "../actions/auth";
import axios  from "axios";
import AsideBar from "./common/AsideBar";
import Footer from "./common/Footer";
import DataTable from "react-data-table-component";

const ManageProducts = ({ auth: { user } }) => {

   const [productsData,setProductsData]=useState([]);
    
    const getProductsData= async()=>{
     try {
      const resp=await axios.get("http://localhost:5000/Fetch-Products");
      setProductsData(resp.data);
     } catch (error) {
      console.log(error);
     }
    }

   const Collumns=[
    {
      name:"Title",
      selector:row=>row.collection_title
    },
    {
      name:"Name",
      selector:row=>row.artCollectionss_name
    },
    {
      name:"Description",
      selector:row=>row.artCollectionss_short_desc

    },
    {
      name:"Category",
      selector:row=>row.artCollectionss_category
    },
    {
      name:"Thumbnail",
      selector:row=><img width={70} height={70} src={`../uploads/${row.collectionsImg}`} alt={"Florida Fashion"} />
    },
    {
      name:"Price",
      selector:row=>row.artCollectionss_price
    },
  
    {
      name:"Type",
      selector:row=>row.type,
    },
    {
      name:"Selling Type",
      selector:row=>row.selling_type
    },    
    {
      name:"Bid Amount(In USD)",
      selector:row=>row.bid_amount_in_usd
    },
    {
      name:"Status",
      selector:row=>row.status
    },
    {
      name:"View",
      cell:(row)=><a href={`/edit?id=${row._id}`} className="btn btn-info d-inline"><i className="fa fa-eye d-inline"></i>View</a>
            
    },  
    {
      name:"Edit",
      cell:(row)=><a href={`/edit?id=${row._id}`} className="btn btn-success"><i className="fa fa-pencil"></i>Edit</a>
            
    },
    {
      name:"Delete",
      cell:(row)=><button className="btn btn-danger"><i className="fa fa-trash"></i>Delet</button>
            
    },  
   ]
     
    useEffect(()=>{
      getProductsData();
    }, []);
  return (
    <div className="wrapper">
      <div>
        <nav className="main-header navbar navbar-expand navbar-white navbar-light">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="pushmenu"
                href="#/"
                role="button"
              >
                <i className="fas fa-bars" />
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block">
              <a href="index3.html" className="nav-link">
                Home
              </a>
            </li>
            <li className="nav-item d-none d-sm-inline-block"></li>
          </ul>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a
                className="nav-link"
                data-widget="navbar-search"
                href="#/"
                role="button"
              >
                <i className="fas fa-search" />
              </a>
              <div className="navbar-search-block">
                <form className="form-inline">
                  <div className="input-group input-group-sm">
                    <input
                      className="form-control form-control-navbar"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    />
                    <div className="input-group-append">
                      <button className="btn btn-navbar" type="submit">
                        <i className="fas fa-search" />
                      </button>
                      <button
                        className="btn btn-navbar"
                        type="button"
                        data-widget="navbar-search"
                      >
                        <i className="fas fa-times" />
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </li>

            <li className="nav-item dropdown">
              <a className="nav-link" data-toggle="dropdown" href="#/">
                <i className="fa fa-user" />
              </a>
              <div className="dropdown-menu dropdown-menu-lg dropdown-menu-right">
                <div style={{ marginTop: "", textAlign: "center" }}>
                  <h5>Welcome, {user && user.name}</h5>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </div>
      <div>
        <AsideBar />
      </div>
      <div className="content-wrapper">
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
              <div className="col-sm-6">
                <h1 className="m-0">Manage Products</h1>
              </div>
            </div>
          </div>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-md-11 m-3" align="right">
              <a className="btn btn-outline-info" href="/admin/artworkCollections"><i className="fa fa-plus"></i> Add Collections</a>
            </div>
          </div>
        </div>
        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-12">
                <DataTable 
                title="Products' Data"
                columns={Collumns}
                data={productsData}
                pagination
                fixedHeader
                selectableRows
                />
              </div>
            </div>
          </div>
        </section>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
};
ManageProducts.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(ManageProducts);
