import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Footer from "../common/Footer";

// import privacyImg from "../../images_files/privacy-policy.jpg"

const PrivacyPolicyComp = ({ isAuthenticated }) => {

    const [PrivacyPolicy, setPrivacyPolicy] = useState("");

    useEffect(() => {
      const fetchPrivacyPolicy = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-PrivacyPolicy");
        const json = await resp.json();
        if (resp.ok) {
          setPrivacyPolicy(json);
        }
      };
  
      fetchPrivacyPolicy();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
          <div className="col-md-12 d-flex justify-content-end">
            <Breadcrumb>
              <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
              <Breadcrumb.Item active>Privacy Policy</Breadcrumb.Item>
            </Breadcrumb>
            </div>
            <div className="col-md-12 bg-primary p-5 my-3" align="center" >
            
             {/* <img src={privacyImg} alt={"About Us"} height={"300px"} width={"100%"}/> */}
             <h2 className="text-dark font-weight-bold">Privacy Policy</h2>
            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {PrivacyPolicy &&
                      PrivacyPolicy.map((PrivacyPolicy) => (
                        <p
                         href=""
                          key={PrivacyPolicy._id}
                        >
                          {PrivacyPolicy.privacyPolicy}
                        </p>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
          <Footer/>
		</section>
	);
};

PrivacyPolicyComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(PrivacyPolicyComp);
