import React,{useState, useEffect}from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Footer from "../common/Footer";

// import aboutusImg from "../../images_files/about-us.jpg"
import Breadcrumb from "react-bootstrap/Breadcrumb";

const ReturnNrefundPolicyComp = ({ isAuthenticated }) => {

    const [ReturnNrefundPolicy, setReturnNrefundPolicy] = useState("");

    useEffect(() => {
      const fetchReturnNrefundPolicy = async () => {
        const resp = await fetch("http://localhost:5000/Fetch-ReturnnRefund");
        const json = await resp.json();
        if (resp.ok) {
          setReturnNrefundPolicy(json);
        }
      };
  
      fetchReturnNrefundPolicy();
    }, []);


	if (isAuthenticated) {
		return <Redirect to="/dashboard" />;
	}




	return (
		<section className="landing">
			 <div className="container-fluid ">
          <div className="row">
            <div className="col-md-12 d-flex justify-content-end">
              <Breadcrumb>
              <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
              <Breadcrumb.Item active> Return & Refund Policy
              </Breadcrumb.Item>
            </Breadcrumb></div>
            <div className="col-md-12 bg-primary my-3 p-5" align="center" >
            
             {/* <img src={aboutusImg} alt={"About Us"} height={"300px"} width={"100%"}/> */}
             <h2 className="text-dark">Return & Refund Policy</h2>
            </div>
          </div>
          </div>
          {/* Slider Part End */}
          <div className="container p-3">
            <div className="row">
              <div className="col-md-12">
              <h3 className="" align="center">
              {ReturnNrefundPolicy &&
                      ReturnNrefundPolicy.map((ReturnNrefundPolicy) => (
                        <p
                         href=""
                          key={ReturnNrefundPolicy._id}
                        >
                          {ReturnNrefundPolicy.returnNrefundPolicy}
                        </p>
                      ))}
                
              </h3>
              </div>
              
            </div>
          </div>
          <Footer/>
		</section>
	);
};

ReturnNrefundPolicyComp.propTypes = {
	isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps)(ReturnNrefundPolicyComp);
