import React, { Fragment,useState } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";
import Logo from "../../images/logo.png";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Modal from 'react-bootstrap/Modal';
// import Metamask from '../../images/meta_mask.png'
import Form from "react-bootstrap/Form";
import axios from "axios";



const NavbaR = ({ auth: { isAuthenticated, loading }, logout }) => {

  // Setstate For Modal
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
   

     
  const [newUser, setNewUser] = useState({
    fname: "",
    lname: "",
    email: "",
    phn: "",
    pass: "",
    role: "",
  });

  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("fname", newUser.fname);
    formData.append("lname", newUser.lname);
    formData.append("email", newUser.email);
    formData.append("phn", newUser.phn);
    formData.append("pass", newUser.pass);
    formData.append("role", newUser.role);

    const addUserRes = axios({
      method: "post",
      url: "http://localhost:5000/userAdding",
      data: JSON.stringify(newUser),
      headers: { "Content-Type": "application/json" },
    });

    if (addUserRes === true) {
      window.location.href = "/admin/dashboard";
    }
  };


    
  const authLinks = (
    <ul className="nav-links list-unstyled">
      <li className="d-inline">
        <Link to="/dashboard">
          <i className="fas fa-user"></i>{" "}
          <span className="hide-sm">Dashboard</span>
        </Link>
      </li>
	  &nbsp; &nbsp;&nbsp;
      <li className="d-inline">
        <Link onClick={logout} to="/" replace>
          <i className="fa fa-arrow-right"></i>{" "}
          <span className="hide-sm">Logout</span>
        </Link>
      </li>
    </ul>
  );
  const guestLinks = (
    <>
    <Navbar expand="lg" className="w-100">
      <Container fluid>
        <Navbar.Brand href="#">
			<h1 align="center">
      <a href="/">
        <img src={Logo} alt="NFT" />
      </a>
    </h1>
	</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            
          </Nav>
          <input type="search" className="search-items d-inline" placeholder="Search Items Here...." /> &nbsp;&nbsp;
        <div className="dropdown d-inline">
            <Button className="m-0 text-dark" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" style={{ background: "#d8ff00", color: "black" }}><i className="fa fa-bars"></i></Button> &nbsp;
            <div class="dropdown-menu p-3 shadow-lg" aria-labelledby="dropdownMenuButton">
                <a class="dropdown-item" href="/">Home</a>
                <a class="dropdown-item" href="/aboutus">About Us</a>
                <a class="dropdown-item" href="/contactus">Contact Us</a>
                <a class="dropdown-item" href="/adminPolicy">Admin Policy</a>
                <a class="dropdown-item" href="/privacy">Privacy Policy</a>
                <a class="dropdown-item" href="/tnc">Terms & Conditions</a>
                <a class="dropdown-item" href="/faqs">FAQs</a>
            </div>
        </div>
        <Button href="/" className="m-0 text-dark d-inline" style={{ background: "#d8ff00", color: "black" }}><i className="fa fa-user"></i></Button> &nbsp;&nbsp;
        <Button href="/" className="m-0 text-dark d-inline" style={{ background: "#d8ff00", color: "black" }}><i className="fa fa-wallet"></i></Button> &nbsp;&nbsp;
        <Button href="/" className="m-0 text-dark d-inline" style={{ background: "#d8ff00", color: "black" }}><i className="fa fa-shopping-cart"></i></Button> &nbsp;&nbsp;
        <Button className="btn btn-outline-warning text-dark d-inline" onClick={handleShow}>Connect To Wallet</Button> &nbsp;&nbsp;
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Kindly Conect To Your Wallet Along With Your Informatons</Modal.Title>
        </Modal.Header>
        <Modal.Body>

        <Form
                className="col-md-12 form-add p-5 shadow-lg rounded"
                onSubmit={handleSubmit}
              >
                <h4>Add User</h4>
                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    style={{
                      background: "aliceblue",
                      color: "black",
                      borderRadius: "22px",
                    }}
                    type="text"
                    name="fname"
                    value={newUser.fname}
                    onChange={handleChange}
                    placeholder="First Name"
                    className="add-users-input"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control
                    style={{
                      background: "aliceblue",
                      color: "black",
                      borderRadius: "22px",
                    }}
                    type="text"
                    name="lname"
                    value={newUser.lname}
                    onChange={handleChange}
                    placeholder="Last Name"
                    className="add-users-input"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    style={{
                      background: "aliceblue",
                      color: "black",
                      borderRadius: "22px",
                    }}
                    type="email"
                    name="email"
                    value={newUser.email}
                    onChange={handleChange}
                    placeholder="Email"
                    className="add-users-input"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Phone Number</Form.Label>
                  <Form.Control
                    style={{
                      background: "aliceblue",
                      color: "black",
                      borderRadius: "22px",
                    }}
                    type="number"
                    name="phn"
                    value={newUser.phn}
                    onChange={handleChange}
                    placeholder="Phone Number"
                    className="add-users-input"
                  />
                </Form.Group>

                <Form.Group className="" controlId="formBasicEmail">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    style={{
                      background: "aliceblue",
                      color: "black",
                      borderRadius: "22px",
                    }}
                    type="text"
                    name="pass"
                    value={newUser.pass}
                    onChange={handleChange}
                    placeholder="Password"
                    className="add-users-input"
                  />
                </Form.Group>

                <Form.Group className="">
                  <Form.Label>Role</Form.Label>
                  <Form.Select
                    style={{
                      background: "aliceblue",
                      color: "black",
                      borderRadius: "22px",
                    }}
                    name="role"
                    value={newUser.role}
                    onChange={handleChange}
                    className="add-users-input"
                  >
                    <option value={"-----"}>-----</option>
                    <option value={"Admin"}>Admin</option>
                    <option value={"User"}>User</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group
                  className=""
                  controlId="formBasicEmail"
                  align="center"
                >
                  <Form.Group
                    className=""
                    controlId="formBasicEmail"
                    align="center"
                  >
                    <Button
                      type="submit"
                      className="btn btn-outline-info text-light w-25 form-control shadow-lg "
                    >
                     Connect
                    </Button>
                  </Form.Group>
                </Form.Group>
              </Form>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          
        </Modal.Footer>
      </Modal>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    </>
  );

  return (
    <nav className="navbar bg-dark" style={{}}>
      {!loading && (
        <Fragment>{isAuthenticated ? authLinks : guestLinks}</Fragment>
      )}
    </nav>
  );
};

Navbar.propTypes = {
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logout })(NavbaR);
